def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """

    class FPNode(object):

        def __init__(self, item, count=1):
            self.item = item
            self.count = count
            self.parent = None
            self.children = defaultdict(FPNode)
            self.link = None

        def add_child(self, child_node):
            self.children[child_node.item] = child_node
            child_node.parent = self

    def build_fp_tree(transactions, min_sup):
        header_table = defaultdict(int)
        for transaction in transactions:
            for item in transaction:
                header_table[item] += 1
        for item in list(header_table.keys()):
            if header_table[item] < min_sup:
                del header_table[item]
        if not header_table:
            return (None, None)
        items = sorted(header_table.keys(), key=lambda x: (-header_table[x], x))
        root = FPNode(None)
        for transaction in transactions:
            filtered_transaction = [item for item in transaction if item in items]
            filtered_transaction.sort(key=lambda x: (-header_table[x], x))
            insert_tree(filtered_transaction, root, header_table)
        return (root, header_table)

    def insert_tree(transaction, root, header_table):
        if not transaction:
            return
        item = transaction[0]
        if item in root.children:
            root.children[item].count += 1
        else:
            new_node = FPNode(item)
            root.add_child(new_node)
            if header_table[item] is None:
                header_table[item] = new_node
            else:
                current_node = header_table[item]
                while current_node.link is not None:
                    current_node = current_node.link
                current_node.link = new_node
        insert_tree(transaction[1:], root.children[item], header_table)

    def find_prefix_path(tree_node):
        path = []
        while tree_node.parent is not None:
            path.append(tree_node)
            tree_node = tree_node.parent
        path.pop()
        return path

    def build_conditional_pattern_base(tree_node):
        conditional_pattern_base = []
        while tree_node is not None:
            prefix_path = find_prefix_path(tree_node)
            if prefix_path:
                conditional_pattern = [node.item for node in prefix_path]
                conditional_pattern_base.append((conditional_pattern, tree_node.count))
            tree_node = tree_node.link
        return conditional_pattern_base

    def mine_tree(tree, header_table, prefix, frequent_itemsets):
        for item in sorted(header_table.keys(), key=lambda x: (-header_table[x], x)):
            new_prefix = prefix + (item,)
            frequent_itemsets.append(new_prefix)
            conditional_pattern_base = build_conditional_pattern_base(header_table[item])
            conditional_transactions = []
            for (pattern, count) in conditional_pattern_base:
                conditional_transactions.append(pattern * count)
            (conditional_tree, conditional_header_table) = build_fp_tree(conditional_transactions, min_sup)
            if conditional_header_table:
                mine_tree(conditional_tree, conditional_header_table, new_prefix, frequent_itemsets)
    (fp_tree, header_table) = build_fp_tree(transactions, min_sup)
    frequent_itemsets = []
    if fp_tree and header_table:
        mine_tree(fp_tree, header_table, (), frequent_itemsets)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
