import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)

    def c(n):
        if n == 1:
            return 0
        else:
            return 2 * np.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def build_tree(subsample):
        if len(subsample) <= 1 or len(subsample) == sample_size:
            return {'size': len(subsample)}
        feature = np.random.choice(data.shape[1])
        min_val = np.min(subsample[:, feature])
        max_val = np.max(subsample[:, feature])
        if min_val == max_val:
            return {'size': len(subsample)}
        threshold = np.random.uniform(min_val, max_val)
        left_subsample = subsample[subsample[:, feature] < threshold]
        right_subsample = subsample[subsample[:, feature] >= threshold]
        return {'feature': feature, 'threshold': threshold, 'left': build_tree(left_subsample), 'right': build_tree(right_subsample)}

    def path_length(tree, x):
        if 'size' in tree:
            return c(tree['size'])
        feature = tree['feature']
        threshold = tree['threshold']
        if x[feature] < threshold:
            return 1 + path_length(tree['left'], x)
        else:
            return 1 + path_length(tree['right'], x)
    trees = [build_tree(data[np.random.choice(data.shape[0], min(sample_size, data.shape[0]), replace=False)]) for _ in range(n_trees)]
    path_lengths = np.zeros(data.shape[0])
    for x in range(data.shape[0]):
        for tree in trees:
            path_lengths[x] += path_length(tree, data[x])
    path_lengths /= n_trees
    phi = 2 * np.log(sample_size - 1) - 2 * (sample_size - 1) / sample_size
    scores = np.power(2, -path_lengths / phi)
    threshold = np.percentile(scores, (1 - epsilon) * 100)
    outliers = [i for (i, score) in enumerate(scores) if score > threshold]
    return sorted(outliers)

