import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    (num_samples, num_features) = X_train.shape
    labels = set(y_train)
    num_labels = len(labels)
    empirical_label_dist = defaultdict(float)
    for label in y_train:
        empirical_label_dist[label] += 1
    for label in empirical_label_dist:
        empirical_label_dist[label] /= num_samples
    empirical_feature_label_dist = defaultdict(lambda : defaultdict(float))
    for (x, y) in zip(X_train, y_train):
        for (feature_index, feature_value) in enumerate(x):
            empirical_feature_label_dist[feature_index, feature_value, y] += 1
    for (feature_index, feature_value, y) in empirical_feature_label_dist:
        empirical_feature_label_dist[feature_index, feature_value, y] /= num_samples
    weights = defaultdict(float)
    for iteration in range(n_iter):
        model_feature_label_dist = defaultdict(float)
        partition_function = defaultdict(float)
        for x in X_train:
            for y in labels:
                score = 0.0
                for (feature_index, feature_value) in enumerate(x):
                    score += weights[feature_index, feature_value, y]
                partition_function[tuple(x)] += math.exp(score)
        for (x, y) in zip(X_train, y_train):
            for (feature_index, feature_value) in enumerate(x):
                score = 0.0
                for y_prime in labels:
                    score_prime = 0.0
                    for (feature_index_prime, feature_value_prime) in enumerate(x):
                        score_prime += weights[feature_index_prime, feature_value_prime, y_prime]
                    partition_function[tuple(x)] += math.exp(score_prime) - math.exp(score)
                model_feature_label_dist[feature_index, feature_value, y] = math.exp(score) / partition_function[tuple(x)]
        max_update = 0.0
        for (feature_index, feature_value, y) in empirical_feature_label_dist:
            empirical_prob = empirical_feature_label_dist[feature_index, feature_value, y]
            model_prob = model_feature_label_dist[feature_index, feature_value, y]
            update = empirical_prob - model_prob
            weights[feature_index, feature_value, y] += update
            max_update = max(max_update, abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for x in X_test:
        max_score = -float('inf')
        predicted_label = None
        for y in labels:
            score = 0.0
            for (feature_index, feature_value) in enumerate(x):
                score += weights[feature_index, feature_value, y]
            if score > max_score:
                max_score = score
                predicted_label = y
        predictions.append(predicted_label)
    return predictions

import numpy as np
def local_outlier_factor(data, k, epsilon=1.5):
    """Implement the Local Outlier Factor (LOF) algorithm from scratch."""
    n = len(data)
    distance_matrix = np.zeros((n, n))
    for i in range(n):
        for j in range(i + 1, n):
            distance = np.sqrt(np.sum((data[i] - data[j]) ** 2))
            distance_matrix[i, j] = distance
            distance_matrix[j, i] = distance
    k_distances = np.zeros(n)
    k_neighbors = [None] * n
    for i in range(n):
        sorted_indices = np.argsort(distance_matrix[i])[1:k + 1]
        k_distances[i] = distance_matrix[i, sorted_indices[-1]]
        k_neighbors[i] = sorted_indices

    def reach_dist_k(p, q):
        return max(k_distances[q], distance_matrix[p, q])
    lrd = np.zeros(n)
    for i in range(n):
        sum_reach_dist = sum((reach_dist_k(i, q) for q in k_neighbors[i]))
        lrd[i] = k / sum_reach_dist
    lof_scores = np.zeros(n)
    for i in range(n):
        sum_lrd_neighbors = sum((lrd[q] for q in k_neighbors[i]))
        lof_scores[i] = sum_lrd_neighbors / k / lrd[i]
    lof_scores = np.round(lof_scores, 4)
    outlier_indices = [i for i in range(n) if lof_scores[i] > epsilon]
    outlier_indices.sort()
    return (list(lof_scores), outlier_indices)

import numpy as np
def kmeans_centroids(X: 'np.ndarray', k: int, max_iters: int=100) -> list[list[float]]:
    """Implement the K-Means clustering algorithm to compute centroids only."""
    centroids = X[:k].copy()
    for _ in range(max_iters):
        distances = np.sum((X[:, np.newaxis] - centroids) ** 2, axis=2)
        labels = np.argmin(distances, axis=1)
        new_centroids = np.array([X[labels == i].mean(axis=0) if np.any(labels == i) else centroids[i] for i in range(k)])
        if np.all(np.linalg.norm(new_centroids - centroids, axis=1) < 0.0001):
            break
        centroids = new_centroids
    sorted_centroids = np.round(centroids[np.lexsort([centroids[:, i] for i in range(centroids.shape[1] - 1, -1, -1)])], 4)
    return sorted_centroids.tolist()
