import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity(mu):
        return mu

    def inv_identity(eta):
        return eta

    def deriv_identity(mu):
        return np.ones_like(mu)

    def log(mu):
        return np.log(mu)

    def inv_log(eta):
        return np.exp(eta)

    def deriv_log(mu):
        return 1.0 / mu

    def logit(mu):
        return np.log(mu / (1 - mu))

    def inv_logit(eta):
        return 1.0 / (1 + np.exp(-eta))

    def deriv_logit(mu):
        return 1.0 / (mu * (1 - mu))
    return {'identity': {'link': identity, 'inv_link': inv_identity, 'deriv': deriv_identity}, 'log': {'link': log, 'inv_link': inv_log, 'deriv': deriv_log}, 'logit': {'link': logit, 'inv_link': inv_logit, 'deriv': deriv_logit}}

