def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation or its derivative.

    Args:
        x (scalar, list, or np.ndarray): Input value(s).
        deriv (bool): If True, compute the derivative (sigmoid) instead of SoftPlus.

    Returns:
        float or list: Rounded result(s) of SoftPlus or its derivative.
    """
    is_scalar = np.isscalar(x)
    x = np.array(x, dtype=float)
    if deriv:
        result = 1 / (1 + np.exp(-x))
    else:
        result = np.log1p(np.exp(-np.abs(x))) + np.maximum(x, 0)
    result = np.round(result, 4)
    if is_scalar:
        return float(result)
    else:
        return result.tolist()

