def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """Implement your code here."""

    def compute_distance(x1, x2, metric):
        if metric == 'euclidean':
            return euclidean(x1, x2)
        elif metric == 'manhattan':
            return cityblock(x1, x2)
        elif metric == 'cosine':
            return cosine(x1, x2)
        else:
            return euclidean(x1, x2)
    predictions = []
    for test_sample in X_test:
        distances = []
        for (train_sample, label) in zip(X_train, y_train):
            distance = compute_distance(test_sample, train_sample, metric)
            distances.append((distance, label))
        neighbors = sorted(distances, key=lambda x: x[0])[:k]
        k_nearest_labels = [label for (_, label) in neighbors]
        most_common = Counter(k_nearest_labels).most_common()
        common_labels = [label for (label, count) in most_common if count == most_common[0][1]]
        predicted_label = min(common_labels)
        predictions.append(predicted_label)
