def apriori_frequent_itemsets(transactions: list[list[Hashable]], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """

    def support(itemset):
        return sum((1 for transaction in transactions if set(itemset).issubset(transaction))) / len(transactions)

    def generate_candidates(frequent_itemsets, k):
        candidates = set()
        for i in frequent_itemsets:
            for j in frequent_itemsets:
                if i[:-1] == j[:-1] and i[-1] < j[-1]:
                    candidate = tuple(sorted(set(i) | set(j)))
                    if all((tuple(sorted(candidate[:len(candidate) - 1] + [item])) in frequent_itemsets for item in candidate)):
                        candidates.add(candidate)
        return candidates
    item_counts = {}
    for transaction in transactions:
        for item in transaction:
            item_counts[item] = item_counts.get(item, 0) + 1
    frequent_itemsets = {item for (item, count) in item_counts.items() if count / len(transactions) >= min_sup}
    frequent_itemsets = {tuple([item]) for item in frequent_itemsets}
    all_frequent_itemsets = list(frequent_itemsets)
    k = 2
    while frequent_itemsets:
        candidates = generate_candidates(frequent_itemsets, k)
        frequent_itemsets = {candidate for candidate in candidates if support(candidate) >= min_sup}
        all_frequent_itemsets.extend(frequent_itemsets)
        k += 1
    all_frequent_itemsets.sort(key=lambda x: (len(x), x))
    return all_frequent_itemsets

