def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    """Cluster *data* with the DBSCAN algorithm.

    Parameters
    ----------
    data : np.ndarray
        Two-dimensional array where each row is a sample and columns are
        features.
    eps : float
        Maximum radius of the neighborhood to be considered reachable.
    min_samples : int
        Minimum number of samples (including the point itself) required to form
        a dense region.

    Returns
    -------
    list[int]
        Cluster labels for each sample. Noise points are labeled ‑1 and cluster
        indices start at 0 and increase sequentially in discovery order.
    """
    if data.size == 0:
        return []

    def euclidean_distance(point1, point2):
        return np.sqrt(np.sum((point1 - point2) ** 2))

    def region_query(point_index):
        neighbors = []
        for i in range(len(data)):
            if euclidean_distance(data[point_index], data[i]) <= eps:
                neighbors.append(i)
        return neighbors

    def expand_cluster(point_index, cluster_id):
        seeds = region_query(point_index)
        if len(seeds) < min_samples:
            labels[point_index] = -1
            return False
        else:
            labels[point_index] = cluster_id
            i = 0
            while i < len(seeds):
                seed = seeds[i]
                if labels[seed] == -1:
                    labels[seed] = cluster_id
                elif labels[seed] == -2:
                    labels[seed] = cluster_id
                    new_seeds = region_query(seed)
                    if len(new_seeds) >= min_samples:
                        seeds += new_seeds
                i += 1
            return True
    labels = [-2] * len(data)
    cluster_id = 0
    for point_index in range(len(data)):
        if labels[point_index] != -2:
            continue
        if expand_cluster(point_index, cluster_id):
            cluster_id += 1
    return labels

import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """

    def build_tree(X, y, depth=0):
        if len(X) < 2 or depth == max_depth:
            return np.mean(y)
        best_split = None
        best_sse = float('inf')
        for i in range(len(X) - 1):
            split_point = (X[i] + X[i + 1]) / 2
            left_indices = [j for j in range(len(X)) if X[j] <= split_point]
            right_indices = [j for j in range(len(X)) if X[j] > split_point]
            left_sse = np.sum((np.array(y)[left_indices] - np.mean(y[left_indices])) ** 2)
            right_sse = np.sum((np.array(y)[right_indices] - np.mean(y[right_indices])) ** 2)
            sse = left_sse + right_sse
            if sse < best_sse:
                best_sse = sse
                best_split = split_point
        if best_split is None:
            return np.mean(y)
        left_indices = [i for i in range(len(X)) if X[i] <= best_split]
        right_indices = [i for i in range(len(X)) if X[i] > best_split]
        left_subtree = build_tree(np.array(X)[left_indices], np.array(y)[left_indices], depth + 1)
        right_subtree = build_tree(np.array(X)[right_indices], np.array(y)[right_indices], depth + 1)
        return (best_split, left_subtree, right_subtree)

    def predict(tree, x):
        if isinstance(tree, float):
            return tree
        (split_point, left_subtree, right_subtree) = tree
        if x <= split_point:
            return predict(left_subtree, x)
        else:
            return predict(right_subtree, x)
    if not X_train:
        return []
    (X_train, y_train) = zip(*sorted(zip(X_train, y_train)))
    tree = build_tree(X_train, y_train)
    return [predict(tree, x) for x in X_test]

