import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """

    class FPNode:

        def __init__(self, item=None, count=0, parent=None):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = defaultdict(FPNode)
            self.link = None

    def build_header_table(frequency):
        return {item: None for item in sorted(frequency, key=lambda x: (-frequency[x], x))}

    def insert_tree(transaction, tree, header_table):
        prefix = transaction
        if not prefix:
            return
        node = tree
        for item in prefix:
            if item in node.children:
                node.children[item].count += 1
            else:
                new_node = FPNode(item, count=1, parent=node)
                node.children[item] = new_node
                if header_table[item] is None:
                    header_table[item] = new_node
                else:
                    current_node = header_table[item]
                    while current_node.link:
                        current_node = current_node.link
                    current_node.link = new_node
            node = node.children[item]

    def find_frequent_items(prefix, node, frequent_itemsets):
        prefix_path = []
        while node.parent:
            prefix_path.append(node.item)
            node = node.parent
        if prefix_path:
            frequent_itemsets.append(tuple(prefix) + tuple(sorted(prefix_path)))
        node = header_table[prefix[-1]].link
        conditional_tree = defaultdict(FPNode)
        while node:
            conditional_prefix_path = []
            parent = node.parent
            while parent.parent:
                conditional_prefix_path.append(parent.item)
                parent = parent.parent
            insert_tree(sorted(conditional_prefix_path, key=lambda x: (-frequency[x], x)), conditional_tree, header_table)
            node = node.link
        if conditional_tree:
            for item in sorted(header_table, key=lambda x: (-frequency[x], x)):
                if conditional_tree.children[item] is not None:
                    find_frequent_itemsets((item,), conditional_tree.children[item], frequent_itemsets)
    frequency = Counter((item for transaction in transactions for item in transaction))
    frequency = {item: count for (item, count) in frequency.items() if count >= min_sup}
    sorted_items = sorted(frequency, key=lambda x: (-frequency[x], x))
    fp_tree = FPNode()
    header_table = build_header_table(frequency)
    for transaction in transactions:
        transaction = [item for item in transaction if item in frequency]
        transaction.sort(key=lambda x: (-frequency[x], x))
        insert_tree(transaction, fp_tree, header_table)
    frequent_itemsets = []
    for item in sorted_items:
        if header_table[item] is not None:
            conditional_tree = defaultdict(FPNode)
            node = header_table[item].link
            while node:
                conditional_prefix_path = []
                parent = node.parent
                while parent.parent:
                    conditional_prefix_path.append(parent.item)
                    parent = parent.parent
                insert_tree(sorted(conditional_prefix_path, key=lambda x: (-frequency[x], x)), conditional_tree, header_table)
                node = node.link
            if conditional_tree:
                find_frequent_itemsets((item,), conditional_tree.children[item], frequent_itemsets)
    frequent_itemsets = sorted(set(frequent_itemsets), key=lambda x: (len(x), x))
    return frequent_itemsets
