import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape

    def c(n):
        if n == 1:
            return 0
        return 2 * np.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def build_tree(subsample, height_limit):
        if height_limit == 0 or len(subsample) <= 1:
            return {'size': len(subsample)}
        feature_idx = np.random.choice(n_features)
        (feature_min, feature_max) = (np.min(subsample[:, feature_idx]), np.max(subsample[:, feature_idx]))
        if feature_min == feature_max:
            return {'size': len(subsample)}
        split_point = np.random.uniform(feature_min, feature_max)
        left_subsample = subsample[subsample[:, feature_idx] < split_point]
        right_subsample = subsample[subsample[:, feature_idx] >= split_point]
        return {'feature_idx': feature_idx, 'split_point': split_point, 'left': build_tree(left_subsample, height_limit - 1), 'right': build_tree(right_subsample, height_limit - 1)}

    def path_length(x, tree, current_height=0):
        if 'size' in tree:
            return current_height + c(tree['size'])
        feature_idx = tree['feature_idx']
        split_point = tree['split_point']
        if x[feature_idx] < split_point:
            return path_length(x, tree['left'], current_height + 1)
        else:
            return path_length(x, tree['right'], current_height + 1)
    trees = [build_tree(np.random.choice(data, min(sample_size, n_samples), replace=False), math.ceil(np.log2(min(sample_size, n_samples)))) for _ in range(n_trees)]
    path_lengths = np.zeros(n_samples)
    for i in range(n_samples):
        x = data[i]
        for tree in trees:
            path_lengths[i] += path_length(x, tree)
    mean_path_lengths = path_lengths / n_trees
    phi = 2 * np.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples
    anomaly_scores = 2 ** (-mean_path_lengths / phi)
    threshold = np.percentile(anomaly_scores, (1 - epsilon) * 100)
    outliers = np.where(anomaly_scores > threshold)[0]
    return sorted(outliers.tolist())

