import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """Predict labels for X_test using a Maximum Entropy Classifier with GIS."""
    unique_features = set()
    unique_labels = set(y_train)
    for sample in X_train:
        for feature in sample:
            unique_features.add(feature)
    empirical_expectations = defaultdict(float)
    model_expectations = defaultdict(float)
    for (x, y) in zip(X_train, y_train):
        for feature in x:
            empirical_expectations[feature, y] += 1
    total_samples = len(X_train)
    for key in empirical_expectations:
        empirical_expectations[key] /= total_samples
    weights = defaultdict(float)
    for iteration in range(n_iter):
        model_expectations.clear()
        Z = {}
        for (i, x) in enumerate(X_train):
            Z_x = 0
            for y in unique_labels:
                score = 0
                for feature in x:
                    score += weights[feature, y]
                Z_x += math.exp(score)
            Z[i] = Z_x
        for (i, (x, y)) in enumerate(zip(X_train, y_train)):
            for feature in x:
                score = 0
                for label in unique_labels:
                    score += weights[feature, label]
                prob = math.exp(score) / Z[i]
                model_expectations[feature, y] += prob
        for key in model_expectations:
            model_expectations[key] /= total_samples
        max_update = 0
        for (feature, y) in empirical_expectations:
            update = empirical_expectations[feature, y] - model_expectations[feature, y]
            weights[feature, y] += update
            max_update = max(max_update, abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for x in X_test:
        max_score = -float('inf')
        best_label = None
        for y in unique_labels:
            score = 0
            for feature in x:
                score += weights[feature, y]
            if score > max_score:
                max_score = score
                best_label = y
        predictions.append(best_label)
    return predictions

import numpy as np
def local_outlier_factor(data, k, epsilon=1.5):
    """Compute the Local Outlier Factor for each sample in the dataset."""
    n_samples = len(data)
    distance_matrix = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(i + 1, n_samples):
            distance = np.sqrt(np.sum((data[i] - data[j]) ** 2))
            distance_matrix[i, j] = distance
            distance_matrix[j, i] = distance
    k_distances = np.zeros(n_samples)
    k_neighbors = [[] for _ in range(n_samples)]
    for i in range(n_samples):
        distances = distance_matrix[i]
        sorted_indices = np.argsort(distances)[1:k + 1]
        k_distances[i] = distances[sorted_indices[-1]]
        k_neighbors[i] = sorted_indices.tolist()

    def reachability_distance(p, q):
        return max(k_distances[q], distance_matrix[p, q])
    lrd = np.zeros(n_samples)
    for p in range(n_samples):
        sum_reach_dist = 0
        for q in k_neighbors[p]:
            sum_reach_dist += reachability_distance(p, q)
        lrd[p] = k / sum_reach_dist
    lof_scores = np.zeros(n_samples)
    for p in range(n_samples):
        sum_lrd_neighbors = 0
        for q in k_neighbors[p]:
            sum_lrd_neighbors += lrd[q]
        lof_scores[p] = sum_lrd_neighbors / k / lrd[p]
    lof_scores_rounded = np.round(lof_scores, 4)
    outlier_indices = np.where(lof_scores_rounded > epsilon)[0]
    outlier_indices_sorted = sorted(outlier_indices)
    return (lof_scores_rounded.tolist(), outlier_indices_sorted)

import numpy as np
def kmeans_centroids(X: 'np.ndarray', k: int, max_iters: int=100) -> list[list[float]]:
    """Implement the K-Means clustering algorithm to compute centroids only."""
    centroids = np.array(X[:k], dtype=float)
    for _ in range(max_iters):
        distances = np.sum((X[:, np.newaxis] - centroids) ** 2, axis=2)
        labels = np.argmin(distances, axis=1)
        new_centroids = np.array([X[labels == i].mean(axis=0) if np.any(labels == i) else centroids[i] for i in range(k)])
        if np.all(np.linalg.norm(new_centroids - centroids, axis=1) < 0.0001):
            break
        centroids = new_centroids
    centroids = np.round(np.sort(centroids, axis=0), 4)
    return centroids.tolist()
