def isolation_tree_path_length(data: np.ndarray) -> list[int]:
    """Compute the depth of the leaf reached by every sample in a random isolation tree.

    The tree is built following the rules described in the task statement and the
    random seed must be set to 0 so that results are reproducible.

    Args:
        data: A 2-D NumPy array of shape (n_samples, n_features).

    Returns:
        A list of length *n_samples* where the *i-th* element is the number of
        edges from the root to the leaf that contains the *i-th* sample.
    """
    np.random.seed(0)

    def build_tree(indices):
        if len(indices) < 3:
            return {'type': 'leaf', 'indices': indices}
        feature_idx = np.random.choice(data.shape[1])
        feature_values = data[indices, feature_idx]
        (down, up) = (feature_values.min(), feature_values.max())
        v = np.random.uniform(down, up)
        left_indices = indices[feature_values <= v]
        right_indices = indices[feature_values > v]
        return {'type': 'internal', 'feature_idx': feature_idx, 'v': v, 'left': build_tree(left_indices), 'right': build_tree(right_indices)}

    def get_depth(node, sample_idx, depth=0):
        if node['type'] == 'leaf':
            return depth
        feature_idx = node['feature_idx']
        v = node['v']
        if data[sample_idx, feature_idx] <= v:
            return get_depth(node['left'], sample_idx, depth + 1)
        else:
            return get_depth(node['right'], sample_idx, depth + 1)
    indices = np.arange(data.shape[0])
    tree = build_tree(indices)
    depths = [get_depth(tree, i) for i in range(data.shape[0])]
    return depths
