def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation function or its derivative.

    Args:
        x (float, list, or np.ndarray): Input values.
        deriv (bool): If True, compute the derivative (sigmoid function) instead.

    Returns:
        float or list: SoftPlus(x) or its gradient, rounded to 4 decimal places.
    """
    x_arr = np.array(x, dtype=float)
    if deriv:
        gradient = 1 / (1 + np.exp(-x_arr))
        result = np.round(gradient, 4)
    else:
        softplus_values = np.where(x_arr > 0, np.log1p(np.exp(-x_arr)) + x_arr, np.log1p(np.exp(x_arr)))
        result = np.round(softplus_values, 4)
    if isinstance(x, (int, float)):
        return float(result[0])
    else:
        return result.tolist()

