def adaboost_predict(X_train: list[list[int | float]], y_train: list[int], X_test: list[list[int | float]], n_estimators: int=10) -> list[int]:
    """Fill in here. The final implementation must follow the specification given in the task
    description and return a list with the predicted class labels for *X_test*."""

    def train_decision_stump(X, y, weights):
        (m, n) = (len(X), len(X[0]))
        best_error = float('inf')
        best_stump = {}
        best_predictions = np.zeros(m)
        for feature_index in range(n):
            thresholds = np.unique([row[feature_index] for row in X])
            for threshold in thresholds:
                for polarity in [1, -1]:
                    predictions = np.ones(m)
                    predictions[X[:, feature_index] * polarity < threshold * polarity] = -1
                    weighted_error = np.sum(weights[predictions != y])
                    if weighted_error < best_error:
                        best_error = weighted_error
                        best_stump = {'feature_index': feature_index, 'threshold': threshold, 'polarity': polarity}
                        best_predictions = predictions.copy()
                        if best_error == 0:
                            return (best_stump, best_predictions, best_error)
        return (best_stump, best_predictions, best_error)
    X_train = np.array(X_train)
    y_train = np.array(y_train) * 2 - 1
    n_samples = len(X_train)
    weights = np.ones(n_samples) / n_samples
    stumps = []
    for _ in range(n_estimators):
        (stump, predictions, error) = train_decision_stump(X_train, y_train, weights)
        if error == 0:
            break
        alpha = 0.5 * np.log((1 - error) / (error + 1e-10))
        weights *= np.exp(-alpha * y_train * predictions)
        weights /= np.sum(weights)
        stump['alpha'] = alpha
        stumps.append(stump)

    def predict_sample(sample):
        stump_predictions = np.array([stump['alpha'] * ((sample[stump['feature_index']] * stump['polarity'] < stump['threshold'] * stump['polarity']) * 2 - 1) for stump in stumps])
        return 1 if np.sum(stump_predictions) > 0 else 0
    return [predict_sample(x) for x in X_test]

