def gini_impurity(y: np.ndarray) -> float:
    """Calculate the Gini impurity for a given array of class labels."""
    m = len(y)
    if m == 0:
        return 0.0
    counts = np.bincount(y)
    probabilities = counts / m
    return 1.0 - np.sum(probabilities ** 2)
def split_data(X: np.ndarray, y: np.ndarray, feature_index: int, threshold: float):
    """Split the dataset into left and right subsets based on a feature and threshold."""
    left_mask = X[:, feature_index] <= threshold
    right_mask = X[:, feature_index] > threshold
    (X_left, y_left) = (X[left_mask], y[left_mask])
    (X_right, y_right) = (X[right_mask], y[right_mask])
    return (X_left, y_left, X_right, y_right)
def best_split(X: np.ndarray, y: np.ndarray) -> dict:
    """Find the best feature and threshold to split the data based on Gini gain."""
    (n_samples, n_features) = X.shape
    best_gini_gain = -1.0
    best_feature = None
    best_threshold = None
    parent_gini = gini_impurity(y)
    for feature_index in range(n_features):
        feature_values = X[:, feature_index]
        unique_values = np.unique(feature_values)
        for i in range(len(unique_values) - 1):
            threshold = (unique_values[i] + unique_values[i + 1]) / 2
            (_, y_left, _, y_right) = split_data(X, y, feature_index, threshold)
            gini_left = gini_impurity(y_left)
            gini_right = gini_impurity(y_right)
            weighted_gini = len(y_left) / n_samples * gini_left + len(y_right) / n_samples * gini_right
            gini_gain = parent_gini - weighted_gini
            if gini_gain > best_gini_gain:
                best_gini_gain = gini_gain
                best_feature = feature_index
                best_threshold = threshold
    return {'feature': best_feature, 'threshold': best_threshold, 'gini_gain': best_gini_gain}
def majority_vote(y: np.ndarray) -> int:
    """Return the majority class label from the array of class labels."""
    return int(np.bincount(y).argmax())
def build_tree(X: np.ndarray, y: np.ndarray, depth: int=0, max_depth: int | None=None):
    """Recursively build a decision tree."""
    if max_depth is not None and depth >= max_depth:
        return majority_vote(y)
    if len(np.unique(y)) == 1:
        return y[0]
    best_sp = best_split(X, y)
    if best_sp['gini_gain'] == 0:
        return majority_vote(y)
    (X_left, y_left, X_right, y_right) = split_data(X, y, best_sp['feature'], best_sp['threshold'])
    left_subtree = build_tree(X_left, y_left, depth + 1, max_depth)
    right_subtree = build_tree(X_right, y_right, depth + 1, max_depth)
    return {'feature': best_sp['feature'], 'threshold': best_sp['threshold'], 'left': left_subtree, 'right': right_subtree}
def predict(tree, X: np.ndarray) -> np.ndarray:
    """Predict class labels for the given dataset using the decision tree."""
    if isinstance(tree, dict):
        feature = tree['feature']
        threshold = tree['threshold']
        left = tree['left']
        right = tree['right']
        if X[feature] <= threshold:
            return predict(left, X)
        else:
            return predict(right, X)
    else:
