        """Check if all spaces in the iterable are instances of Box."""
        return all((isinstance(space, Box) for space in spaces))

    def is_space_continuous(space: Space, is_tuple: bool) -> bool:
        """Determine if a space is continuous based on its type and sub-spaces."""
        if isinstance(space, Box):
            return True
        if is_tuple:
            if isinstance(space, Tuple):
                return all_boxes(space.spaces)
            elif isinstance(space, Dict):
                return all_boxes(space.spaces.values())
        return False
    cont_action = is_space_continuous(env.action_space, tuple_action)
    cont_obs = is_space_continuous(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor.

    Parameters
    ----------
    X_train : np.ndarray
        2-D array with shape (n_samples, n_features) containing the training
        features.
    y_train : np.ndarray
        1-D array of length n_samples containing the training targets.
    X_test : np.ndarray
        2-D array with shape (m_samples, n_features) containing the test
        features to predict.
    n_estimators : int, default=10
        Number of boosting iterations.
    learning_rate : float, default=0.1
        Shrinkage factor applied to each tree’s prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """

    def mean_squared_error(y_true, y_pred):
        return np.mean((y_true - y_pred) ** 2)

    def best_split(X, y):
        (best_feature, best_threshold, best_error) = (None, None, float('inf'))
        (n_samples, n_features) = X.shape
        for feature in range(n_features):
            thresholds = np.unique(X[:, feature])
            for threshold in thresholds:
                left_mask = X[:, feature] <= threshold
                right_mask = X[:, feature] > threshold
                if np.any(left_mask) and np.any(right_mask):
                    left_pred = np.mean(y[left_mask])
                    right_pred = np.mean(y[right_mask])
                    left_error = mean_squared_error(y[left_mask], left_pred)
                    right_error = mean_squared_error(y[right_mask], right_pred)
                    error = (left_error * np.sum(left_mask) + right_error * np.sum(right_mask)) / n_samples
                    if error < best_error:
                        best_error = error
                        best_feature = feature
                        best_threshold = threshold
        return (best_feature, best_threshold)

    class TreeNode:

        def __init__(self, feature=None, threshold=None, left=None, right=None, value=None):
            self.feature = feature
            self.threshold = threshold
            self.left = left
            self.right = right
            self.value = value

        def predict(self, X):
            if self.value is not None:
                return self.value
            if X[self.feature] <= self.threshold:
                return self.left.predict(X)
            else:
                return self.right.predict(X)

    def build_tree(X, y, depth=0):
        if depth >= max_depth or len(np.unique(y)) == 1:
            return TreeNode(value=np.mean(y))
        (feature, threshold) = best_split(X, y)
        if feature is None or threshold is None:
            return TreeNode(value=np.mean(y))
        left_mask = X[:, feature] <= threshold
        right_mask = X[:, feature] > threshold
        left_tree = build_tree(X[left_mask], y[left_mask], depth + 1)
        right_tree = build_tree(X[right_mask], y[right_mask], depth + 1)
        return TreeNode(feature=feature, threshold=threshold, left=left_tree, right=right_tree)

    def predict_tree(trees, X):
        pred = np.full(X.shape[0], np.mean(y_train))
        for tree in trees:
            pred += learning_rate * np.array([tree.predict(x) for x in X])
        return pred
    trees = []
    y_pred = np.full_like(y_train, np.mean(y_train))
    for _ in range(n_estimators):
        residuals = y_train - y_pred
        tree = build_tree(X_train, residuals, 0)
        trees.append(tree)
        y_pred += learning_rate * predict_tree([tree], X_train)
    final_pred = predict_tree(trees, X_test)
    return [round(pred, 4) for pred in final_pred]

