import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """

    def sse(y):
        mean = np.mean(y)
        return np.sum((y - mean) ** 2)

    def best_split(X, y):
        min_sse = float('inf')
        best_split_value = None
        for i in range(1, len(X)):
            left_y = y[:i]
            right_y = y[i:]
            current_sse = sse(left_y) + sse(right_y)
            if current_sse < min_sse:
                min_sse = current_sse
                best_split_value = (X[i] + X[i - 1]) / 2
        return (best_split_value, min_sse)

    def build_tree(X, y, depth):
        if depth == max_depth or len(y) < 2:
            return np.mean(y)
        (split_value, _) = best_split(X, y)
        left_indices = X < split_value
        right_indices = X >= split_value
        left_subtree = build_tree(X[left_indices], y[left_indices], depth + 1)
        right_subtree = build_tree(X[right_indices], y[right_indices], depth + 1)
        return (split_value, left_subtree, right_subtree)

    def predict_tree(x, tree):
        if not isinstance(tree, tuple):
            return tree
        (split_value, left_subtree, right_subtree) = tree
        if x < split_value:
            return predict_tree(x, left_subtree)
        else:
            return predict_tree(x, right_subtree)
    if not X_train or not y_train:
        return []
    sorted_indices = np.argsort(X_train)
    X_train_sorted = np.array(X_train)[sorted_indices]
    y_train_sorted = np.array(y_train)[sorted_indices]
    tree = build_tree(X_train_sorted, y_train_sorted, 0)
    return [predict_tree(x, tree) for x in X_test]

import numpy as np
def sigmoid(z):
    """Compute the numerically stable, element-wise sigmoid activation.

    The function must work for a scalar, list/tuple, or NumPy ndarray input.

    Args:
        z: int, float, list, tuple, or numpy.ndarray – input values.

    Returns:
        float if *z* is a scalar, otherwise a Python list obtained with
        ``numpy.ndarray.tolist()``, where every element is the sigmoid of the
        corresponding input value and is rounded to four decimal places.
    """
    if isinstance(z, (int, float)):
        z_array = np.array([z])
    else:
        z_array = np.array(z)
    sigmoid_values = np.where(z_array > 0, 1 / (1 + np.exp(-z_array)), np.exp(z_array) / (1 + np.exp(z_array)))
    sigmoid_values_rounded = np.round(sigmoid_values, 4)
    if isinstance(z, (int, float)):
        return float(sigmoid_values_rounded[0])
    else:
        return sigmoid_values_rounded.tolist()

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def compute_gradient(value):
        return np.round(value * (1 - value), 4)
    if isinstance(z, (int, float)):
        return float(compute_gradient(z))
    elif isinstance(z, list):
        if not z:
            return []
        return [sigmoid_grad(item) for item in z]
    elif isinstance(z, np.ndarray):
        return np.vectorize(compute_gradient)(z).tolist()
    else:
        raise ValueError('Input must be a float, list, or NumPy ndarray.')

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    current_node = root
    while current_node.y is None:
        if task_type == 'classification':
            feature_value = features[current_node.label]
            found_child = False
            for child in current_node.child:
                if child.x == feature_value:
                    current_node = child
                    found_child = True
                    break
            if not found_child:
                current_node = current_node.child[1]
        elif task_type == 'regression':
            feature_value = features[current_node.label]
            if feature_value <= current_node.s:
                current_node = current_node.child[0]
            else:
                current_node = current_node.child[1]
        else:
            raise ValueError("task_type must be either 'classification' or 'regression'")
    return current_node.y

