import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)

    def c(n):
        if n <= 1:
            return 0
        return 2 * np.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def build_tree(X):
        if len(X) <= 1 or sample_size == 1:
            return (None, len(X))
        depth = 0
        height_limit = math.ceil(np.log2(sample_size))
        while depth < height_limit and len(X) > 1:
            feature_idx = np.random.randint(X.shape[1])
            (min_val, max_val) = (X[:, feature_idx].min(), X[:, feature_idx].max())
            if min_val == max_val:
                break
            split_val = np.random.uniform(min_val, max_val)
            left_mask = X[:, feature_idx] < split_val
            if np.any(left_mask) and np.any(~left_mask):
                (left_tree, left_size) = build_tree(X[left_mask])
                (right_tree, right_size) = build_tree(X[~left_mask])
                return ((feature_idx, split_val, left_tree, right_tree), None)
            depth += 1
        return (None, len(X))

    def path_length(x, tree, current_depth=0):
        if tree is None:
            return current_depth + c(sample_size)
        (feature_idx, split_val, left_tree, right_tree) = tree
        if x[feature_idx] < split_val:
            return path_length(x, left_tree, current_depth + 1)
        else:
            return path_length(x, right_tree, current_depth + 1)
    n_samples = data.shape[0]
    path_lengths = np.zeros(n_samples)
    for _ in range(n_trees):
        if sample_size >= n_samples:
            (tree, _) = build_tree(data)
        else:
            sampled_indices = np.random.choice(n_samples, sample_size, replace=False)
            (tree, _) = build_tree(data[sampled_indices])
        for i in range(n_samples):
            path_lengths[i] += path_length(data[i], tree)
    avg_path_lengths = path_lengths / n_trees
    phi = 2 * np.log(sample_size - 1) - 2 * (sample_size - 1) / sample_size
    anomaly_scores = 2 ** (-avg_path_lengths / phi)
    num_outliers = int(epsilon * n_samples)
    outlier_indices = np.argsort(anomaly_scores)[-num_outliers:]
    return sorted(outlier_indices.tolist())

