import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    labels = np.unique(y_train)
    num_labels = len(labels)
    feature_indices = np.arange(X_train.shape[1])
    feature_values = {i: set(X_train[:, i]) for i in feature_indices}
    empirical_expectations = defaultdict(float)
    for (x, y) in zip(X_train, y_train):
        for feature_index in feature_indices:
            value = x[feature_index]
            empirical_expectations[feature_index, value, y] += 1
    total_samples = len(X_train)
    for key in empirical_expectations:
        empirical_expectations[key] /= total_samples
    weights = defaultdict(float)
    for iteration in range(n_iter):
        expected_counts = defaultdict(float)
        model_expectations = defaultdict(float)
        for x in X_train:
            Z_x = 0
            for y in labels:
                score = 0
                for feature_index in feature_indices:
                    value = x[feature_index]
                    score += weights[feature_index, value, y]
                Z_x += math.exp(score)
            for y in labels:
                score = 0
                for feature_index in feature_indices:
                    value = x[feature_index]
                    score += weights[feature_index, value, y]
                p_y_given_x = math.exp(score) / Z_x
                for feature_index in feature_indices:
                    value = x[feature_index]
                    expected_counts[feature_index, value, y] += p_y_given_x
                    model_expectations[feature_index, value, y] += p_y_given_x
        for key in expected_counts:
            expected_counts[key] /= total_samples
        max_update = 0
        for key in weights:
            update = math.log(empirical_expectations[key] / model_expectations[key])
            weights[key] += update
            max_update = max(max_update, abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for x in X_test:
        max_score = -float('inf')
        predicted_label = None
        for y in labels:
            score = 0
            for feature_index in feature_indices:
                value = x[feature_index]
                score += weights[feature_index, value, y]
            if score > max_score:
                max_score = score
                predicted_label = y
        predictions.append(predicted_label)
    return predictions

import numpy as np
def local_outlier_factor(data, k, epsilon=1.5):
    """Compute the Local Outlier Factor (LOF) for each sample in the dataset."""
    n_samples = data.shape[0]
    distance_matrix = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            if i != j:
                distance_matrix[i, j] = np.linalg.norm(data[i] - data[j])
    k_distances = np.zeros(n_samples)
    k_neighbor_indices = np.zeros((n_samples, k), dtype=int)
    for i in range(n_samples):
        distances = distance_matrix[i]
        sorted_indices = np.argsort(distances)
        k_neighbor_indices[i] = sorted_indices[1:k + 1]
        k_distances[i] = distances[sorted_indices[k]]

    def reachability_distance(k_distance, distance):
        return max(k_distance, distance)
    lrd = np.zeros(n_samples)
    for i in range(n_samples):
        neighbors = k_neighbor_indices[i]
        reachability_distances = [reachability_distance(k_distances[n], distance_matrix[i, n]) for n in neighbors]
        lrd[i] = k / np.sum(reachability_distances)
    lof_scores = np.zeros(n_samples)
    for i in range(n_samples):
        neighbors = k_neighbor_indices[i]
        lof_scores[i] = np.sum(lrd[neighbors]) / k / lrd[i]
    lof_scores_rounded = np.round(lof_scores, 4).tolist()
    outlier_indices = np.where(lof_scores > epsilon)[0].tolist()
    return (lof_scores_rounded, sorted(outlier_indices))
