def softplus(x, deriv: bool=False):
    """Computes the SoftPlus activation or its gradient on the input x."""

    def _softplus(val):
        if val > 30:
            return val + np.log1p(np.exp(-val))
        else:
            return np.log1p(np.exp(val))

    def _sigmoid(val):
        if val < -30:
            return np.exp(val)
        else:
            return 1 / (1 + np.exp(-val))
    if isinstance(x, (list, np.ndarray)):
        x = np.array(x)
        if deriv:
            result = np.vectorize(_sigmoid)(x)
        else:
            result = np.vectorize(_softplus)(x)
        return np.round(result, 4).tolist()
    elif deriv:
        return round(_sigmoid(x), 4)
    else:
        return round(_softplus(x), 4)

