def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth.

    A *transaction* is represented by a list of items (strings).  `min_support`
    is the minimum number of transactions an item-set has to appear in.  The
    function returns **all** frequent item-sets where
        support(itemset) >= min_support.

    The result must be deterministic:
      • Inside each item-set the items have to be sorted alphabetically.
      • The outer list has to be sorted by `(len(itemset), itemset)`.
    If *transactions* is empty or no item-set meets the threshold return an
    empty list.
    """
    if not transactions:
        return []
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item: support for (item, support) in item_counts.items() if support >= min_support}
    if not frequent_items:
        return []
    frequent_items = dict(sorted(frequent_items.items(), key=lambda x: (-x[1], x[0])))
    fp_tree = FPTree()
    for transaction in transactions:
        filtered_transaction = [item for item in transaction if item in frequent_items]
        filtered_transaction.sort(key=lambda x: frequent_items[x], reverse=True)
        fp_tree.add_transaction(filtered_transaction)
    frequent_itemsets = []
    for (base_pat, value) in fp_tree.header_table.items():
        support_count = value['support']
        frequent_itemsets.append([base_pat])
        conditional_base_pat = []
        node = value['tail']
        while node:
            prefix_path = []
            parent = node.parent
            while parent and parent.item:
                prefix_path.append(parent.item)
                parent = parent.parent
            if prefix_path:
                conditional_base_pat.append((prefix_path, node.support))
            node = node.link
        if conditional_base_pat:
            cond_tree = FPTree()
            for (path, support) in conditional_base_pat:
                cond_tree.add_transaction(path, support)
            cond_tree.header_table = {v: fp_tree.header_table[v] for v in cond_tree.header_table if cond_tree.header_table[v]['support'] >= min_support}
            if cond_tree.header_table:
                mined_sets = fp_growth([t for (t, _) in conditional_base_pat], min_support)
                for item_set in mined_sets:
                    frequent_itemsets.append(item_set + [base_pat])
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets

from collections import deque
def escape_fire_maze(grid: list[str]) -> int:
    """Escape from a maze with spreading fire.

    Parameters
    ----------
    grid : list[str]
        Square maze represented as a list of strings. Each character must be
        '.', '#', or 'F'.  The agent starts at the upper-left corner (0,0) and
        wishes to reach the lower-right corner (n-1,n-1).  Fire starts in every
        cell marked with 'F' and spreads to the four neighbouring cells every
        minute.  The fire spreads first, then the agent moves.

    Returns
    -------
    int
        Minimum number of minutes required for the agent to reach the goal
        without entering a burning cell, or -1 if this is impossible.
    """
    n = len(grid)
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    if n == 1 and grid[0][0] == '.':
        return 0
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_queue.append((i, j))
    fire_time = [[math.inf] * n for _ in range(n)]
    for (i, j) in fire_queue:
        fire_time[i][j] = 0
    while fire_queue:
        (x, y) = fire_queue.popleft()
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] == '.'):
                if fire_time[nx][ny] == math.inf:
                    fire_time[nx][ny] = fire_time[x][y] + 1
                    fire_queue.append((nx, ny))
    agent_queue = deque([(0, 0, 0)])
    visited = {(0, 0, 0)}
    while agent_queue:
        (x, y, t) = agent_queue.popleft()
        if x == n - 1 and y == n - 1:
            return t
        for (dx, dy) in directions:
            (nx, ny) = (x + dx, y + dy)
            if 0 <= nx < n and 0 <= ny < n and (grid[nx][ny] == '.'):
                if (nx, ny, t + 1) not in visited and t + 1 < fire_time[nx][ny]:
                    visited.add((nx, ny, t + 1))
                    agent_queue.append((nx, ny, t + 1))
    return -1

