import numpy as np
def gini_impurity(y):
    """Calculate the Gini impurity of a list of class labels."""
    m = len(y)
    if m == 0:
        return 0.0
    counts = np.bincount(y)
    probabilities = counts / m
    gini = 1.0 - np.sum(probabilities ** 2)
    return gini
def split_dataset(X, y, feature_index, threshold):
    """Split the dataset based on a feature and a threshold."""
    left_mask = X[:, feature_index] <= threshold
    right_mask = X[:, feature_index] > threshold
    (X_left, y_left) = (X[left_mask], y[left_mask])
    (X_right, y_right) = (X[right_mask], y[right_mask])
    return (X_left, y_left, X_right, y_right)
def best_split(X, y):
    """Find the best split by evaluating all possible splits."""
    (best_feature_index, best_threshold, best_gini_gain) = (None, None, -1)
    parent_gini = gini_impurity(y)
    (num_samples, num_features) = X.shape
    for feature_index in range(num_features):
        unique_values = np.unique(X[:, feature_index])
        for i in range(1, len(unique_values)):
            threshold = (unique_values[i] + unique_values[i - 1]) / 2.0
            (X_left, y_left, X_right, y_right) = split_dataset(X, y, feature_index, threshold)
            if len(y_left) == 0 or len(y_right) == 0:
                continue
            left_gini = gini_impurity(y_left)
            right_gini = gini_impurity(y_right)
            weighted_gini = len(y_left) / num_samples * left_gini + len(y_right) / num_samples * right_gini
            gini_gain = parent_gini - weighted_gini
            if gini_gain > best_gini_gain:
                best_gini_gain = gini_gain
                best_feature_index = feature_index
                best_threshold = threshold
    return (best_feature_index, best_threshold)
def build_tree(X, y, depth, max_depth):
    """Recursively build the decision tree."""
    if max_depth is not None and depth >= max_depth:
        return np.bincount(y).argmax()
    if len(np.unique(y)) == 1:
        return y[0]
    (feature_index, threshold) = best_split(X, y)
    if feature_index is None:
        return np.bincount(y).argmax()
    (X_left, y_left, X_right, y_right) = split_dataset(X, y, feature_index, threshold)
    left_node = build_tree(X_left, y_left, depth + 1, max_depth)
    right_node = build_tree(X_right, y_right, depth + 1, max_depth)
    return (feature_index, threshold, left_node, right_node)
def predict_tree(node, row):
    """Make prediction for a single row given a trained tree."""
    if isinstance(node, int):
        return node
    (feature_index, threshold, left_node, right_node) = node
    if row[feature_index] <= threshold:
        return predict_tree(left_node, row)
    else:
        return predict_tree(right_node, row)
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""
    tree = build_tree(X_train, y_train, 0, max_depth)
    predictions = [predict_tree(tree, row) for row in X_test]
    return predictions

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """

    def validate_vectors(x, y):
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except ValueError:
            return False
        if x.ndim != 1 or y.ndim != 1 or x.size == 0 or (y.size == 0) or (x.size != y.size):
            return False
        return True

    def euclidean(x, y):
        return np.sqrt(np.sum((x - y) ** 2))

    def manhattan(x, y):
        return np.sum(np.abs(x - y))

    def chebyshev(x, y):
        return np.max(np.abs(x - y))

    def cosine(x, y):
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        if norm_x == 0 or norm_y == 0:
            return -1
        return 1 - np.dot(x, y) / (norm_x * norm_y)

    def d(x, y):
        if not validate_vectors(x, y):
            return -1
        if name == 'euclidean':
            return round(euclidean(x, y), 4)
        elif name == 'manhattan':
            return round(manhattan(x, y), 4)
        elif name == 'chebyshev':
            return round(chebyshev(x, y), 4)
        elif name == 'cosine':
            return round(cosine(x, y), 4)
        else:
            return -1
    return d

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def hand_value(hand: list[int]) -> int:
        """Calculate the highest legal value of a hand."""
        value = sum(hand)
        aces = hand.count(1)
        while aces and value + 10 <= 21:
            value += 10
            aces -= 1
        return value

    def is_natural_blackjack(hand: list[int]) -> bool:
        """Check if the hand is a natural blackjack."""
        return len(hand) == 2 and 1 in hand and (10 in hand)
    player_bust = hand_value(player) > 21
    dealer_bust = hand_value(dealer) > 21
    if player_bust:
        return -1
    if dealer_bust:
        return 1
    player_score = hand_value(player)
    dealer_score = hand_value(dealer)
    if player_score == dealer_score:
        return 0
    elif player_score > dealer_score:
        if is_natural_blackjack(player):
            return 1.5
        return 1
    else:
        return -1

