def sigmoid(z):
    """Compute the element-wise sigmoid (logistic) function.

    The function must work for scalar numbers, Python lists and NumPy arrays and
    must be numerically stable for very large positive or negative inputs.

    Args:
        z: A scalar (int/float) or array-like object (list or np.ndarray)
           containing numeric values.

    Returns:
        float | list: The sigmoid of the input rounded to 4 decimal places.
        If `z` is a scalar the return value is a float, otherwise it is a Python
        list with the same shape as the input.
    """

    def sigmoid_scalar(x):
        if x >= 0:
            return round(1 / (1 + np.exp(-x)), 4)
        else:
            return round(np.exp(x) / (1 + np.exp(x)), 4)
    if isinstance(z, (int, float)):
        return sigmoid_scalar(z)
    elif isinstance(z, (list, np.ndarray)):
        z = np.array(z)
        result = np.where(z >= 0, 1 / (1 + np.exp(-z)), np.exp(z) / (1 + np.exp(z)))
        return np.round(result, 4).tolist()
    else:
        raise ValueError('Input must be a scalar, list, or numpy array.')

import numpy as np
def spectral_clustering(X: np.ndarray, k: int) -> list[int]:
    """Cluster data using Spectral Clustering.

    Args:
        X: 2-D NumPy array where each row is a data sample.
        k: Desired number of clusters.

    Returns:
        List of length n_samples containing an integer cluster label for each sample.
    """
    (n, d) = X.shape
    W = np.zeros((n, n))
    for i in range(n):
        for j in range(n):
            if i != j:
                W[i, j] = 1 / (1 + np.linalg.norm(X[i] - X[j]))
    D = np.diag(np.sum(W, axis=1))
    L = D - W
    (eigenvalues, eigenvectors) = np.linalg.eigh(L)
    E = eigenvectors[:, :k]
    centroids = E[:k].copy()
    labels = np.zeros(n, dtype=int)
    for _ in range(100):
        for i in range(n):
            distances = np.linalg.norm(E[i] - centroids, axis=1)
            labels[i] = np.argmin(distances)
        new_centroids = np.zeros_like(centroids)
        cluster_counts = np.zeros(k, dtype=int)
        for i in range(n):
            new_centroids[labels[i]] += E[i]
            cluster_counts[labels[i]] += 1
        for i in range(k):
            if cluster_counts[i] == 0:
                new_centroids[i] = E[np.random.choice(n)]
            else:
                new_centroids[i] /= cluster_counts[i]
        if np.all(centroids == new_centroids):
            break
        centroids = new_centroids
    return labels.tolist()

import numpy as np
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.  See the detailed task
    description for the exact algorithm that has to be implemented.
    """
    if max_freq is None:
        max_freq = fs / 2

    def hz_to_mel(f):
        return 2595 * np.log10(1 + f / 700)

    def mel_to_hz(m):
        return 700 * (10 ** (m / 2595) - 1)
    mel_min = hz_to_mel(min_freq)
    mel_max = hz_to_mel(max_freq)
    mel_points = np.linspace(mel_min, mel_max, n_filters + 2)
    hz_points = mel_to_hz(mel_points)
    filter_bank = np.zeros((n_filters, N // 2 + 1))
    for i in range(n_filters):
        (left, center, right) = (hz_points[i], hz_points[i + 1], hz_points[i + 2])
        for k in range(N // 2 + 1):
            freq = k * fs / N
            if left < freq <= center:
                filter_bank[i, k] = (freq - left) / (center - left)
            elif center < freq < right:
                filter_bank[i, k] = (right - freq) / (right - center)
        if normalize:
            filter_bank[i] *= 2 / (hz_points[i + 2] - hz_points[i])
    filter_bank = filter_bank.tolist()
    filter_bank = [[round(w, 4) for w in row] for row in filter_bank]
    return filter_bank

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool) -> Tuple[bool, bool]:
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def is_space_continuous(space: Space, is_tuple: bool) -> bool:
        if is_tuple:
            if isinstance(space, Tuple):
                return all((isinstance(sub_space, Box) for sub_space in space.spaces))
            elif isinstance(space, Dict):
                return all((isinstance(sub_space, Box) for sub_space in space.spaces.values()))
            else:
                return False
        else:
            return isinstance(space, Box)
    cont_action = is_space_continuous(env.action_space, tuple_action)
    cont_obs = is_space_continuous(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

