        Shrinkage factor applied to each tree’s prediction.
    max_depth : int, default=3
        Maximum depth of every individual regression tree.

    Returns
    -------
    list[float]
        Predictions for every sample in *X_test*, rounded to 4 decimal places.
    """

    def mean_squared_error(y_true, y_pred):
        return np.mean((y_true - y_pred) ** 2)

    def best_split(X, y):
        min_mse = np.inf
        best_feature = None
        best_threshold = None
        for feature_idx in range(X.shape[1]):
            thresholds = np.unique(X[:, feature_idx])
            for threshold in thresholds:
                y_left = y[X[:, feature_idx] <= threshold]
                y_right = y[X[:, feature_idx] > threshold]
                mse_left = mean_squared_error(y_left, np.mean(y_left)) if y_left.size > 0 else 0
                mse_right = mean_squared_error(y_right, np.mean(y_right)) if y_right.size > 0 else 0
                mse = (mse_left * y_left.size + mse_right * y_right.size) / y.size
                if mse < min_mse:
                    min_mse = mse
                    best_feature = feature_idx
                    best_threshold = threshold
        return (best_feature, best_threshold)

    def build_tree(X, y, depth=0):
        if depth >= max_depth or len(np.unique(y)) == 1:
            return np.mean(y)
        (best_feature, best_threshold) = best_split(X, y)
        if best_feature is None or best_threshold is None:
            return np.mean(y)
        X_left = X[X[:, best_feature] <= best_threshold]
        y_left = y[X[:, best_feature] <= best_threshold]
        X_right = X[X[:, best_feature] > best_threshold]
        y_right = y[X[:, best_feature] > best_threshold]
        left_tree = build_tree(X_left, y_left, depth + 1)
        right_tree = build_tree(X_right, y_right, depth + 1)
        return (best_feature, best_threshold, left_tree, right_tree)

    def predict_tree(tree, X):
        if isinstance(tree, np.float64):
            return tree
        (feature_idx, threshold, left_tree, right_tree) = tree
        if X[feature_idx] <= threshold:
            return predict_tree(left_tree, X)
        else:
            return predict_tree(right_tree, X)

    def predict_trees(trees, X):
        return np.sum([predict_tree(tree, X) for tree in trees], axis=0)
    F = np.full(y_train.shape, np.mean(y_train))
    trees = []
    for _ in range(n_estimators):
        residuals = y_train - F
        tree = build_tree(X_train, residuals)
        trees.append(tree)
        F += learning_rate * predict_trees([tree], X_train)
    final_predictions = np.mean(y_train) + learning_rate * predict_trees(trees, X_test)
    return [round(pred, 4) for pred in final_predictions.tolist()]

