import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_counts = Counter((item for transaction in transactions for item in transaction))
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_sup}
    if not frequent_items:
        return []
    frequent_item_sorted = sorted(frequent_items.keys(), key=lambda x: (frequent_items[x], x), reverse=True)
    item_to_rank = {item: rank for (rank, item) in enumerate(frequent_item_sorted)}

    def insert_tree(transaction, tree, header_table):
        if not transaction:
            return
        item = transaction[0]
        if item in tree:
            tree[item]['count'] += 1
        else:
            tree[item] = {'count': 1, 'children': {}, 'parent': tree}
            if item in header_table:
                link = header_table[item]['tail']
                while link['next']:
                    link = link['next']
                link['next'] = tree[item]
            else:
                header_table[item] = {'head': tree[item], 'tail': tree[item]}
        insert_tree(transaction[1:], tree[item]['children'], header_table)
    fp_tree = {}
    header_table = {}
    for transaction in transactions:
        sorted_trans = sorted((item for item in transaction if item in frequent_items), key=lambda item: item_to_rank[item])
        insert_tree(sorted_trans, fp_tree, header_table)

    def find_frequent_patterns(tree, header_table, prefix, freq_items):
        for (item, item_info) in sorted(header_table.items(), key=lambda x: item_to_rank[x[0]]):
            support_count = item_info['tail']['count']
            pattern = prefix + (item,)
            freq_items.append(pattern)
            conditional_db = []
            node = item_info['tail']
            while node:
                path = []
                parent = node['parent']
                while parent != tree:
                    path.append(parent['item'])
                    parent = parent['parent']
                conditional_db.append((path, node['count']))
                node = node['next']
            conditional_tree = {}
            conditional_header_table = {}
            for (cond_path, cond_count) in conditional_db:
                cond_path = list(filter(lambda x: x in frequent_items, cond_path))
                cond_path.sort(key=lambda x: item_to_rank[x], reverse=True)
                insert_tree(cond_path, conditional_tree, conditional_header_table)
            if conditional_tree:
                find_frequent_patterns(conditional_tree, conditional_header_table, pattern, freq_items)
    frequent_itemsets = []
    find_frequent_patterns(fp_tree, header_table, (), frequent_itemsets)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
