from collections import deque
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> list[int]:
    """Density-Based Spatial Clustering (DBSCAN).

    Parameters
    ----------
    data : np.ndarray
        A 2-D array with shape (n_samples, n_features).
    eps : float
        Radius that defines the neighbourhood of a point.
    min_samples : int
        Minimum number of points required to form a dense region (core point).

    Returns
    -------
    list[int]
        Cluster labels for every sample.  Noise points receive the label –1
        while clusters are numbered 1, 2, 3, … in the order they are found.
    """
    n_samples = data.shape[0]
    labels = [-1] * n_samples

    def euclidean_distance(p1, p2):
        return np.sqrt(np.sum((p1 - p2) ** 2))
    neighborhoods = {}
    for i in range(n_samples):
        neighborhoods[i] = []
        for j in range(n_samples):
            if i != j and euclidean_distance(data[i], data[j]) <= eps:
                neighborhoods[i].append(j)
    cluster_id = 0

    def expand_cluster(point, cluster_id):
        labels[point] = cluster_id
        queue = deque([point])
        while queue:
            current_point = queue.popleft()
            if len(neighborhoods[current_point]) >= min_samples:
                for neighbor in neighborhoods[current_point]:
                    if labels[neighbor] == -1:
                        labels[neighbor] = cluster_id
                    if labels[neighbor] == 0:
                        labels[neighbor] = cluster_id
                        queue.append(neighbor)
    for point in range(n_samples):
        if labels[point] != -1:
            continue
        if len(neighborhoods[point]) < min_samples:
            labels[point] = -1
        else:
            cluster_id += 1
            expand_cluster(point, cluster_id)
    return labels

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    current_node = root
    while current_node.y is None:
        if task_type == 'classification':
            feature_value = features[current_node.label]
            found_child = False
            for child in current_node.child:
                if child.x == feature_value:
                    current_node = child
                    found_child = True
                    break
            if not found_child:
                current_node = current_node.child[1]
        elif task_type == 'regression':
            feature_value = features[current_node.label]
            if feature_value <= current_node.s:
                current_node = current_node.child[0]
            else:
                current_node = current_node.child[1]
    return current_node.y

import numpy as np
import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)

    def c(n):
        if n <= 1:
            return 0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def path_length(x, tree, depth=0):
        if isinstance(tree, tuple):
            (feature, threshold, left, right) = tree
            if x[feature] < threshold:
                return path_length(x, left, depth + 1)
            else:
                return path_length(x, right, depth + 1)
        else:
            return depth + c(tree)

    def build_tree(subsample):
        if len(subsample) <= 1 or depth == height_limit:
            return len(subsample)
        feature = np.random.choice(n_features)
        (min_val, max_val) = (np.min(subsample[:, feature]), np.max(subsample[:, feature]))
        if min_val == max_val:
            return len(subsample)
        threshold = np.random.uniform(min_val, max_val)
        left_subsample = subsample[subsample[:, feature] < threshold]
        right_subsample = subsample[subsample[:, feature] >= threshold]
        left_tree = build_tree(left_subsample)
        right_tree = build_tree(right_subsample)
        return (feature, threshold, left_tree, right_tree)
    (n_samples, n_features) = data.shape
    height_limit = math.ceil(math.log2(sample_size if sample_size < n_samples else n_samples))
    trees = [build_tree(np.random.choice(n_samples, sample_size, replace=False)) for _ in range(n_trees)]
    path_lengths = np.zeros(n_samples)
    for i in range(n_samples):
        for tree in trees:
            path_lengths[i] += path_length(data[i], tree)
    avg_path_length = path_lengths / n_trees
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples
    anomaly_scores = 2 ** (-avg_path_length / phi)
    threshold = np.percentile(anomaly_scores, (1 - epsilon) * 100)
    outliers = np.where(anomaly_scores > threshold)[0]
    return sorted(outliers.tolist())
