from typing import List, Dict, Any
def entropy(class_counts: Dict[Any, int]) -> float:
    """Calculate the entropy of a set of class counts."""
    total = sum(class_counts.values())
    if total == 0:
        return 0.0
    return -sum((count / total * math.log2(count / total) for count in class_counts.values()))
def information_gain(parent_entropy: float, left_counts: Dict[Any, int], right_counts: Dict[Any, int]) -> float:
    """Calculate the information gain from splitting the data."""
    total = sum(left_counts.values()) + sum(right_counts.values())
    if total == 0:
        return 0.0
    left_weight = sum(left_counts.values()) / total
    right_weight = sum(right_counts.values()) / total
    return parent_entropy - (left_weight * entropy(left_counts) + right_weight * entropy(right_counts))
def most_common_class(examples: List[Dict[str, Any]], target_attr: str) -> Any:
    """Return the most common class in the examples."""
    return Counter((example[target_attr] for example in examples)).most_common(1)[0][0]
def learn_decision_tree(examples: List[Dict[str, Any]], attributes: List[str], target_attr: str) -> Dict[str, Any]:
    """
    Learn a decision tree using entropy and information gain.
    Returns a nested dictionary representing the decision tree.
    """
    if all((example[target_attr] == examples[0][target_attr] for example in examples)):
        return {'class': examples[0][target_attr]}
    if not attributes:
        return {'class': most_common_class(examples, target_attr)}
    best_attr = None
    best_gain = -1.0
    parent_entropy = entropy(Counter((example[target_attr] for example in examples)))
    for attr in attributes:
        attr_values = set((example[attr] for example in examples))
        splits = {value: [ex for ex in examples if ex[attr] == value] for value in attr_values}
        left_counts = Counter((example[target_attr] for example in splits[next(iter(attr_values))]))
        right_counts = Counter((example[target_attr] for example in splits[next(reversed(attr_values))]))
        gain = information_gain(parent_entropy, left_counts, right_counts)
        if gain > best_gain:
            best_gain = gain
            best_attr = attr
    if best_gain <= 0:
        return {'class': most_common_class(examples, target_attr)}
    tree = {'attribute': best_attr}
    remaining_attributes = [a for a in attributes if a != best_attr]
    attr_values = set((example[best_attr] for example in examples))
    for value in attr_values:
        subtree_examples = [ex for ex in examples if ex[best_attr] == value]
        tree[value] = learn_decision_tree(subtree_examples, remaining_attributes, target_attr)
    return tree

class Value:

    def __init__(self, data, _children=(), _op=''):
        self.data = data
        self.grad = 0
        self._backward = lambda : None
        self._prev = set(_children)
        self._op = _op

    def __add__(self, other):
        other = other if isinstance(other, Value) else Value(other)
        out = Value(self.data + other.data, _children=(self, other), _op='add')

        def _backward():
            self.grad += out.grad * 1
            other.grad += out.grad * 1
        out._backward = _backward
        return out

    def __mul__(self, other):
        other = other if isinstance(other, Value) else Value(other)
        out = Value(self.data * other.data, _children=(self, other), _op='mul')

        def _backward():
            self.grad += out.grad * other.data
            other.grad += out.grad * self.data
        out._backward = _backward
        return out

    def relu(self):
        out = Value(self.data if self.data > 0 else 0, _children=(self,), _op='relu')

        def _backward():
            self.grad += (out.data > 0) * out.grad
        out._backward = _backward
        return out

    def backward(self):
        topo = []
        visited = set()

        def build_topo(v):
            if v not in visited:
                visited.add(v)
                for child in v._prev:
                    build_topo(child)
                topo.append(v)
        build_topo(self)
        self.grad = 1
        for node in reversed(topo):
            node._backward()

    def __repr__(self):
        return f'Value(data={self.data}, grad={self.grad})'

