class Dense(Layer):

    def __init__(self, n_units, input_shape=None):
        self.layer_input = None
        self.input_shape = input_shape
        self.n_units = n_units
        self.trainable = True
        self.W = None
        self.w0 = None
        self.optimizer_W = None
        self.optimizer_w0 = None

    def initialize(self, optimizer):
        limit = 1 / math.sqrt(self.input_shape[0])
        self.W = np.random.uniform(-limit, limit, size=(self.input_shape[0], self.n_units))
        self.w0 = np.zeros((1, self.n_units))
        self.optimizer_W = copy.copy(optimizer)
        self.optimizer_w0 = copy.copy(optimizer)

    def parameters(self):
        return self.W.size + self.w0.size

    def forward_pass(self, X, training):
        self.layer_input = X
        output = np.dot(X, self.W) + self.w0
        return output

    def backward_pass(self, accum_grad):
        W = self.W
        if W is None:
            raise Exception('Weights not initialized before backward pass')
        grad_W = np.dot(self.layer_input.T, accum_grad)
        grad_w0 = np.sum(accum_grad, axis=0, keepdims=True)
        if self.trainable:
            self.W = self.optimizer_W.update(self.W, grad_W)
            self.w0 = self.optimizer_w0.update(self.w0, grad_w0)
        grad_input = np.dot(accum_grad, W.T)
        return grad_input

    def output_shape(self):
        return (self.n_units,)

import numpy as np
def simple_conv2d(input_matrix: np.ndarray, kernel: np.ndarray, padding: int, stride: int):
    (in_height, in_width) = input_matrix.shape
    (kernel_height, kernel_width) = kernel.shape
    out_height = (in_height + 2 * padding - kernel_height) // stride + 1
    out_width = (in_width + 2 * padding - kernel_width) // stride + 1
    padded_input = np.pad(input_matrix, ((padding, padding), (padding, padding)), mode='constant')
    output = np.zeros((out_height, out_width))
    for i in range(out_height):
        for j in range(out_width):
            region = padded_input[i * stride:i * stride + kernel_height, j * stride:j * stride + kernel_width]
            output[i, j] = np.sum(region * kernel)
    return np.round(output, 4).tolist()

import numpy as np
def compute_tf_idf(corpus, query):
    if not corpus:
        raise ValueError('The corpus is empty. Cannot compute TF-IDF.')
    vocabulary = set()
    for doc in corpus:
        vocabulary.update(doc)
    vocabulary.update(query)
    vocabulary = list(vocabulary)
    vocab_size = len(vocabulary)
    word_to_index = {word: i for (i, word) in enumerate(vocabulary)}
    num_docs = len(corpus)
    tf_matrix = np.zeros((num_docs, vocab_size))
    for (doc_idx, doc) in enumerate(corpus):
        word_counts = {}
        total_words = len(doc)
        for word in doc:
            word_counts[word] = word_counts.get(word, 0) + 1
        for (word, count) in word_counts.items():
            tf_matrix[doc_idx, word_to_index[word]] = count / total_words if total_words > 0 else 0
    df_vector = np.zeros(vocab_size)
    for (i, word) in enumerate(vocabulary):
        count = 0
        for doc in corpus:
            if word in doc:
                count += 1
        df_vector[i] = count
    idf_vector = np.log((num_docs + 1) / (df_vector + 1) + 1e-10)
    tf_idf_matrix = tf_matrix * idf_vector
    tf_idf_matrix = np.round(tf_idf_matrix, 4)
    return tf_idf_matrix.tolist()

def compressed_col_sparse_matrix(dense_matrix):
    """
    Convert a dense matrix into its Compressed Column Sparse (CSC) representation.

    :param dense_matrix: List of lists representing the dense matrix
    :return: Tuple of (values, row indices, column pointer)
    """
    if not dense_matrix or not dense_matrix[0]:
        return ([], [], [0])
    num_rows = len(dense_matrix)
    num_cols = len(dense_matrix[0])
    values = []
    row_indices = []
    col_ptr = [0]
    for col in range(num_cols):
        for row in range(num_rows):
            val = dense_matrix[row][col]
            if val != 0:
                values.append(val)
                row_indices.append(row)
        col_ptr.append(len(values))
    return (values, row_indices, col_ptr)

def calculate_brightness(img):
    if not img or not img[0]:
        return -1
    expected_length = len(img[0])
    total_brightness = 0
    total_pixels = 0
    for row in img:
        if len(row) != expected_length:
            return -1
        for pixel in row:
            if not isinstance(pixel, int) or not 0 <= pixel <= 255:
                return -1
            total_brightness += pixel
            total_pixels += 1
    average_brightness = total_brightness / total_pixels
