def cosine_similarity(v1, v2):
    if v1.shape != v2.shape:
        raise ValueError('Input vectors must have the same shape.')
    if v1.size == 0 or v2.size == 0:
        raise ValueError('Input vectors cannot be empty.')
    dot_product = np.dot(v1, v2)
    magnitude_v1 = np.linalg.norm(v1)
    magnitude_v2 = np.linalg.norm(v2)
    if magnitude_v1 == 0 or magnitude_v2 == 0:
        raise ValueError('Input vectors cannot have zero magnitude.')
    similarity = dot_product / (magnitude_v1 * magnitude_v2)
    return round(similarity, 3)

import numpy as np
def pos_encoding(position: int, d_model: int):
    if position == 0 or d_model <= 0:
        return -1
    pe = np.zeros((position, d_model), dtype=np.float16)
    for pos in range(position):
        for i in range(0, d_model, 2):
            div_term = np.exp(np.float16(-np.log(10000.0) * (i // 2) / d_model))
            pe[pos, i] = np.sin(pos * div_term)
            if i + 1 < d_model:
                pe[pos, i + 1] = np.cos(pos * div_term)
    return pe.tolist()

