def elu(x: float, alpha: float=1.0) -> float:
    """
    Compute the ELU activation function.

    Args:
        x (float): Input value
        alpha (float): ELU parameter for negative values (default: 1.0)

    Returns:
        float: ELU activation value
    """
    if x > 0:
        return round(x, 4)
    else:
        return round(alpha * (math.exp(x) - 1), 4)

import math
def softplus(x: float) -> float:
    """
    Compute the softplus activation function.

    Args:
        x: Input value

    Returns:
        The softplus value: log(1 + e^x)
    """
    if x > 20:
        return round(x, 4)
    elif x < -20:
        return round(math.exp(x), 4)
    else:
        return round(math.log(1 + math.exp(x)), 4)

def min_max(x: list[int]) -> list[float]:
    if not x:
        return []
    min_val = min(x)
    max_val = max(x)
    if min_val == max_val:
        return [0.0 for _ in x]
    normalized = [(val - min_val) / (max_val - min_val) for val in x]
    return [round(val, 4) for val in normalized]

