from typing import Dict, List, Any, Union, Tuple
def entropy(class_counts: Dict[Any, int]) -> float:
    """
    Calculate the entropy of a list of class counts.
    """
    total = sum(class_counts.values())
    if total == 0:
        return 0.0
    return -sum((count / total * math.log(count / total, 2) for count in class_counts.values() if count > 0))
def information_gain(parent_entropy: float, left_counts: Dict[Any, int], right_counts: Dict[Any, int]) -> float:
    """
    Calculate the information gain from splitting a dataset into two parts.
    """
    total = sum(left_counts.values()) + sum(right_counts.values())
    if total == 0:
        return 0.0
    left_weight = sum(left_counts.values()) / total
    right_weight = sum(right_counts.values()) / total
    return parent_entropy - (left_weight * entropy(left_counts) + right_weight * entropy(right_counts))
def get_majority_class(examples: List[Dict[str, Any]], target_attr: str) -> Any:
    """
    Return the most common target attribute value among examples.
    """
    class_values = [example[target_attr] for example in examples]
    return Counter(class_values).most_common(1)[0][0]
def all_same_class(examples: List[Dict[str, Any]], target_attr: str) -> bool:
    """
    Check if all examples have the same target attribute value.
    """
    first_class = examples[0][target_attr]
    for example in examples:
        if example[target_attr] != first_class:
            return False
    return True
def split_dataset(examples: List[Dict[str, Any]], attribute: str, value: Any) -> Tuple[List[Dict[str, Any]], List[Dict[str, Any]]]:
    """
    Split the examples into two sets based on the value of `attribute`.
    """
    true_set = [example for example in examples if example[attribute] == value]
    false_set = [example for example in examples if example[attribute] != value]
    return (true_set, false_set)
def learn_decision_tree(examples: List[Dict[str, Any]], attributes: List[str], target_attr: str) -> Union[Dict, Any]:
    """
    Build a decision tree using recursive binary splitting based on entropy and information gain.
    Returns a nested dictionary representing the decision tree.
    """
    if not examples:
        return get_majority_class(examples, target_attr)
    if all_same_class(examples, target_attr):
        return examples[0][target_attr]
    if not attributes:
        return get_majority_class(examples, target_attr)
    best_gain = -1
    best_attribute = None
    best_value = None
    best_splits = None
    parent_entropy = entropy(Counter((example[target_attr] for example in examples)))
    for attr in attributes:
        if attr == target_attr:
            continue
        unique_values = set((example[attr] for example in examples))
        for val in unique_values:
            (true_split, false_split) = split_dataset(examples, attr, val)
            if len(true_split) == 0 or len(false_split) == 0:
                continue
            left_classes = Counter((example[target_attr] for example in true_split))
            right_classes = Counter((example[target_attr] for example in false_split))
            gain = information_gain(parent_entropy, left_classes, right_classes)
            if gain > best_gain:
                best_gain = gain
                best_attribute = attr
                best_value = val
                best_splits = (true_split, false_split)
    if best_gain <= 0:
        return get_majority_class(examples, target_attr)
    attributes.remove(best_attribute)
    true_tree = learn_decision_tree(best_splits[0], attributes.copy(), target_attr)
    false_tree = learn_decision_tree(best_splits[1], attributes.copy(), target_attr)
    return {'attribute': best_attribute, 'value': best_value, 'true': true_tree, 'false': false_tree}
