def to_categorical(x, n_col=None):
    if n_col is None:
        n_col = np.max(x) + 1 if x.size > 0 else 0
    if x.size == 0:
        return np.zeros((0, n_col), dtype=int).tolist()
    categorical = np.zeros((x.size, n_col), dtype=int)
    categorical[np.arange(x.size), x] = 1
    return categorical.tolist()
