def compute_tf_idf(corpus, query):
    if not corpus:
        raise ValueError('The corpus cannot be empty.')
    vocabulary = set()
    for doc in corpus:
        vocabulary.update(doc)
    vocabulary.update(query)
    vocabulary = sorted(list(vocabulary))
    tf_matrix = np.zeros((len(corpus), len(vocabulary)))
    for (i, doc) in enumerate(corpus):
        if not doc:
            continue
        word_counts = {}
        total_words = len(doc)
        for word in doc:
            word_counts[word] = word_counts.get(word, 0) + 1
        for (j, term) in enumerate(vocabulary):
            tf = word_counts.get(term, 0) / total_words if total_words > 0 else 0
            tf_matrix[i, j] = tf
    df = np.zeros(len(vocabulary))
    for (j, term) in enumerate(vocabulary):
        count = 0
        for doc in corpus:
            if term in doc:
                count += 1
        df[j] = np.log((len(corpus) + 1) / (count + 1))
    tf_idf = tf_matrix * df
    tf_idf_rounded = np.round(tf_idf, 4)
    return tf_idf_rounded.tolist()
