def calculate_brightness(img):
    if not img:
        return -1
    rows = len(img)
    cols = len(img[0])
    for row in img:
        if len(row) != cols:
            return -1
    total_pixels = rows * cols
    total_brightness = 0
    for row in img:
        for pixel in row:
            if not 0 <= pixel <= 255:
                return -1
            total_brightness += pixel
    average = total_brightness / total_pixels
    return round(average, 2)
