def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemsets (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    N = len(transactions)
    min_support_count = math.ceil(minsup * N)
    transaction_sets = [set(transaction) for transaction in transactions]
    item_counts = Counter()
    for items in transaction_sets:
        for item in items:
            item_counts[frozenset([item])] += 1
    frequent_itemsets = {tuple(sorted(item)): count for (item, count) in item_counts.items() if count >= min_support_count}
    k = 2
    current_candidates = set((frozenset(items) for items in frequent_itemsets.keys()))
    while current_candidates:
        next_candidates = set()
        for a in current_candidates:
            for b in current_candidates:
                if len(a.symmetric_difference(b)) == 2:
                    candidate = a.union(b)
                    if len(candidate) == k:
                        next_candidates.add(frozenset(candidate))
        if not next_candidates:
            break
        candidate_counts = Counter()
        for candidate in next_candidates:
            candidate_set = set(candidate)
            for transaction in transaction_sets:
                if candidate_set.issubset(transaction):
                    candidate_counts[candidate] += 1
        for (candidate, count) in candidate_counts.items():
            if count >= min_support_count:
                frequent_itemsets[tuple(sorted(candidate))] = count
        current_candidates = next_candidates
        k += 1
    return frequent_itemsets
