def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation function or its derivative (sigmoid).

    Args:
        x: A scalar, list, or NumPy array of real values.
        deriv: If True, compute the derivative (sigmoid) instead of SoftPlus.

    Returns:
        A Python list of results, with each value rounded to 4 decimal places.
        If input is a scalar, returns a single float.
    """
    x = np.array(x, dtype=np.float64)
    if deriv:
        exp_neg_x = np.exp(-np.abs(x))
        sigmoid = 1 / (1 + exp_neg_x)
        sigmoid = np.where(x > 0, 1 - exp_neg_x, exp_neg_x)
        result = sigmoid
    else:
        result = np.where(x > 0, x + np.log1p(np.exp(-x)), np.log1p(np.exp(x)))
    result = np.round(result, 4)
    if x.ndim == 0:
        return float(result.item())
    else:
        return result.tolist()

