def adaboost_predict(X_train: List[List[int | float]], y_train: List[int], X_test: List[List[int | float]], n_estimators: int=10) -> List[int]:
    """Train AdaBoost with decision stumps and predict on test data."""
    X_train = np.array(X_train)
    y_train = np.array(y_train)
    X_test = np.array(X_test)
    y_train = np.where(y_train == 0, -1, 1)
    (n_samples, n_features) = X_train.shape
    weights = np.ones(n_samples) / n_samples
    stumps = []
    alphas = []
    for _ in range(n_estimators):
        best_error = float('inf')
        best_stump = None
        for feature_idx in range(n_features):
            unique_vals = np.unique(X_train[:, feature_idx])
            for threshold in unique_vals:
                predictions = np.where(X_train[:, feature_idx] < threshold, 1, -1)
                error = np.sum(weights * (predictions != y_train))
                if error < best_error:
                    best_error = error
                    best_stump = {'feature_idx': feature_idx, 'threshold': threshold, 'polarity': 1}
                elif abs(error - best_error) < 1e-10:
                    if feature_idx < best_stump['feature_idx'] or (feature_idx == best_stump['feature_idx'] and threshold < best_stump['threshold']) or (feature_idx == best_stump['feature_idx'] and threshold == best_stump['threshold'] and (1 < best_stump['polarity'])):
                        best_error = error
                        best_stump = {'feature_idx': feature_idx, 'threshold': threshold, 'polarity': 1}
                predictions = np.where(X_train[:, feature_idx] >= threshold, 1, -1)
                error = np.sum(weights * (predictions != y_train))
                if error < best_error:
                    best_error = error
                    best_stump = {'feature_idx': feature_idx, 'threshold': threshold, 'polarity': -1}
                elif abs(error - best_error) < 1e-10:
                    if feature_idx < best_stump['feature_idx'] or (feature_idx == best_stump['feature_idx'] and threshold < best_stump['threshold']) or (feature_idx == best_stump['feature_idx'] and threshold == best_stump['threshold'] and (-1 < best_stump['polarity'])):
                        best_error = error
                        best_stump = {'feature_idx': feature_idx, 'threshold': threshold, 'polarity': -1}
        if best_error <= 1e-10:
            break
        error_t = best_error
        alpha_t = 0.5 * np.log((1 - error_t) / (error_t + 1e-10))
        stump = best_stump
        predictions = np.where(X_train[:, stump['feature_idx']] < stump['threshold'], 1, -1)
        if stump['polarity'] == -1:
            predictions = -predictions
        weights *= np.exp(-alpha_t * y_train * predictions)
        weights /= np.sum(weights)
        stumps.append(stump)
        alphas.append(alpha_t)
    test_predictions = np.zeros(X_test.shape[0])
    for (stump, alpha) in zip(stumps, alphas):
        predictions = np.where(X_test[:, stump['feature_idx']] < stump['threshold'], 1, -1)
        if stump['polarity'] == -1:
            predictions = -predictions
        test_predictions += alpha * predictions
    final_predictions = np.where(test_predictions >= 0, 1, 0)
    return final_predictions.tolist()

