    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    freq_items = Counter()
    for transaction in transactions:
        freq_items.update(set(transaction))
    freq_items = {item: count for (item, count) in freq_items.items() if count >= min_sup}
    if not freq_items:
        return []
    sorted_items = sorted(freq_items.keys(), key=lambda x: (-freq_items[x], x))
    item_to_index = {item: idx for (idx, item) in enumerate(sorted_items)}

    def order_transaction(transaction):
        return [item for item in transaction if item in freq_items]
    ordered_transactions = [order_transaction(t) for t in transactions]
    ordered_transactions = [t for t in ordered_transactions if t]

    class FPNode:

        def __init__(self, item, count, parent):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}

        def increment(self, count):
            self.count += count

        def __repr__(self):
            return f'{self.item}: {self.count}'

    def build_fp_tree(transactions, item_to_index):
        root = FPNode(None, 0, None)
        header_table = defaultdict(list)
        for transaction in transactions:
            if not transaction:
                continue
            sorted_transaction = sorted(transaction, key=lambda x: (-freq_items[x], item_to_index[x]))
            update_tree(sorted_transaction, root, header_table)
        for item in list(header_table.keys()):
            if sum((node.count for node in header_table[item])) < min_sup:
                del header_table[item]
        return (root, header_table)

    def update_tree(items, node, header_table):
        if not items:
            return
        item = items[0]
        rest = items[1:]
        if item in node.children:
            child = node.children[item]
            child.increment(1)
        else:
            child = FPNode(item, 1, node)
            node.children[item] = child
            if item in header_table:
                header_table[item].append(child)
            else:
                header_table[item] = [child]
        update_tree(rest, child, header_table)
    (root, header_table) = build_fp_tree(ordered_transactions, item_to_index)

    def mine_tree(header_table, prefix, freq_itemsets):
        items = sorted(header_table.keys(), key=lambda x: (-sum((node.count for node in header_table[x])), x))
        for item in items:
            new_prefix = prefix + (item,)
            freq_itemsets.append(tuple(sorted(new_prefix)))
            conditional_patterns = []
            for node in header_table[item]:
                path = []
                current = node.parent
                while current and current.item is not None:
                    path.append((current.item, current.count))
                    current = current.parent
                if path:
                    path.reverse()
                    conditional_patterns.append((path, node.count))

            def cond_trans_to_freq_items(conditional_patterns):
                cond_freq_items = Counter()
                for (path, count) in conditional_patterns:
                    for (item, _) in path:
                        cond_freq_items[item] += count
                return cond_freq_items
            cond_freq_items = cond_trans_to_freq_items(conditional_patterns)
            if not cond_freq_items:
                continue
            cond_sorted_items = sorted(cond_freq_items.keys(), key=lambda x: (-cond_freq_items[x], x))
            cond_item_to_index = {item: idx for (idx, item) in enumerate(cond_sorted_items)}
            cond_transactions = []
            for (path, count) in conditional_patterns:
                cond_trans = [(item, count) for (item, _) in path if item in cond_freq_items]
                if cond_trans:
                    cond_trans.sort(key=lambda x: (-cond_freq_items[x[0]], cond_item_to_index[x[0]]))
                    cond_transactions.append([(item, count) for (item, count) in cond_trans])

            def build_cond_tree(cond_transactions, cond_item_to_index):
                cond_root = FPNode(None, 0, None)
                cond_header_table = defaultdict(list)
                for trans in cond_transactions:
                    items = [item for (item, _) in trans]
                    counts = [count for (_, count) in trans]
                    update_cond_tree(items, counts, cond_root, cond_header_table)
                return (cond_root, cond_header_table)

            def update_cond_tree(items, counts, node, header_table):
                if not items:
                    return
                item = items[0]
                count = counts[0]
                rest_items = items[1:]
                rest_counts = counts[1:]
                if item in node.children:
                    child = node.children[item]
                    child.increment(count)
                else:
                    child = FPNode(item, count, node)
                    node.children[item] = child
                    if item in header_table:
                        header_table[item].append(child)
                    else:
                        header_table[item] = [child]
                update_cond_tree(rest_items, rest_counts, child, header_table)
            (cond_root, cond_header_table) = build_cond_tree(cond_transactions, cond_item_to_index)
            mine_tree(cond_header_table, new_prefix, freq_itemsets)
    freq_itemsets = []
    mine_tree(header_table, (), freq_itemsets)
    freq_itemsets.sort(key=lambda x: (len(x), x))
    return freq_itemsets

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def process(x):
        if isinstance(x, (float, int)):
            return round(x * (1 - x), 4)
        elif isinstance(x, np.ndarray):
            arr = np.round(x * (1 - x), 4)
            if arr.ndim == 0:
                return float(arr)
            elif arr.ndim == 1:
                return [float(val) for val in arr]
            else:
                return [[float(val) for val in row] for row in arr]
        elif isinstance(x, list):
            return [process(item) for item in x]
        else:
            raise TypeError('Unsupported input type: {}'.format(type(x)))
    return process(z)

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    feature_value = features[root.label]
    if task_type == 'classification':
        for child in root.child:
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import numpy as np
import math
from typing import List
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> List[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if epsilon == 0:
        return []

    def c(n):
        if n <= 1:
            return 0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def compute_phi(n):
        return 2 * math.log(n - 1) - 2 * (n - 1) / n

    class IsolationTree:

        def __init__(self, data, height_limit):
            self.height_limit = height_limit
            self.n_features = data.shape[1]
            self.root = self.build_tree(data, 0)

        def build_tree(self, data, depth):
            n = data.shape[0]
            if depth == self.height_limit or n <= 1:
                return {'type': 'leaf', 'size': n}
            else:
                feature_idx = np.random.randint(self.n_features)
                min_val = np.min(data[:, feature_idx])
                max_val = np.max(data[:, feature_idx])
                threshold = np.random.uniform(min_val, max_val)
                left_data = data[data[:, feature_idx] < threshold]
                right_data = data[data[:, feature_idx] >= threshold]
                left = self.build_tree(left_data, depth + 1)
                right = self.build_tree(right_data, depth + 1)
                return {'type': 'node', 'feature': feature_idx, 'threshold': threshold, 'left': left, 'right': right}

        def get_path_length(self, x):
            return self._get_path_length(self.root, x, 0)

        def _get_path_length(self, node, x, depth):
            if node['type'] == 'leaf':
                size = node['size']
                return depth + c(size)
            elif x[node['feature']] < node['threshold']:
                return self._get_path_length(node['left'], x, depth + 1)
            else:
                return self._get_path_length(node['right'], x, depth + 1)

    def compute_height_limit(sample_size):
        return math.ceil(math.log2(sample_size))
    trees = []
    for _ in range(n_trees):
        if sample_size >= n_samples:
            subsample = data
        else:
            indices = np.random.choice(n_samples, size=sample_size, replace=False)
            subsample = data[indices]
        height_limit = compute_height_limit(sample_size)
        tree = IsolationTree(subsample, height_limit)
        trees.append(tree)
    avg_path_lengths = np.zeros(n_samples)
    for i in range(n_samples):
        x = data[i]
        total_length = 0
        for tree in trees:
            total_length += tree.get_path_length(x)
        avg_path_lengths[i] = total_length / n_trees
    phi = compute_phi(n_samples)
    scores = 2 ** (-(avg_path_lengths / phi))
    n_outliers = max(1, int(np.ceil(epsilon * n_samples)))
    threshold = np.partition(scores, -n_outliers)[-n_outliers]
    outlier_indices = [i for (i, score) in enumerate(scores) if score >= threshold]
    outlier_indices.sort()
    return outlier_indices
