class FPTreeNode:

    def __init__(self, name: str, count: int, parent):
        self.name = name
        self.count = count
        self.parent = parent
        self.children = {}
        self.node_link = None

    def increment(self, count: int):
        self.count += count
def build_fp_tree(transactions: List[List[str]], min_support: int) -> Tuple[FPTreeNode, Dict[str, FPTreeNode]]:
    """Build the FP-tree from the transactions."""
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return (None, {})
    sorted_items = sorted(frequent_items, key=lambda x: (-item_counts[x], x))
    item_order = {item: idx for (idx, item) in enumerate(sorted_items)}

    def reorder_transaction(transaction):
        return [item for item in transaction if item in frequent_items]
    filtered_transactions = [reorder_transaction(t) for t in transactions]
    filtered_transactions = [t for t in filtered_transactions if t]
    root = FPTreeNode('null', 1, None)
    header_table = {}
    for transaction in filtered_transactions:
        if not transaction:
            continue
        ordered_items = sorted(transaction, key=lambda x: (item_order[x], x))
        update_tree(ordered_items, root, header_table, 1)
    return (root, header_table)
def update_tree(items: List[str], node: FPTreeNode, header_table: Dict[str, FPTreeNode], count: int):
    """Recursively update the FP-tree with a transaction."""
    if not items:
        return
    first_item = items[0]
    if first_item in node.children:
        child = node.children[first_item]
        child.increment(count)
    else:
        child = FPTreeNode(first_item, count, node)
        node.children[first_item] = child
        if first_item not in header_table:
            header_table[first_item] = child
        else:
            while header_table[first_item].node_link is not None:
                header_table[first_item] = header_table[first_item].node_link
            header_table[first_item].node_link = child
    update_tree(items[1:], child, header_table, count)
def find_frequent_itemsets(transactions: List[List[str]], min_support: int) -> List[List[str]]:
    """Main function to find all frequent itemsets using FP-Growth."""
    (root, header_table) = build_fp_tree(transactions, min_support)
    if root is None:
        return []
    frequent_itemsets = [item for item in header_table]

    def mine_tree(header_table, prefix, frequent_itemsets):
        ordered_items = sorted(header_table.keys(), key=lambda x: (len(header_table[x]), x))
        for item in ordered_items:
            new_prefix = prefix + [item]
            new_prefix.sort()
            frequent_itemsets.append(new_prefix)
            conditional_patterns = []
            conditional_supports = []
            node = header_table[item]
            while node is not None:
                path = []
                support = node.count
                current = node.parent
                while current.name != 'null':
                    path.append(current.name)
                    current = current.parent
                if path:
                    conditional_patterns.append(path[::-1])
                    conditional_supports.append(support)
                node = node.node_link
            (conditional_tree, conditional_header) = build_fp_tree(conditional_patterns, min_support)
            if conditional_header:
                mine_tree(conditional_header, new_prefix, frequent_itemsets)
    mine_tree(header_table, [], frequent_itemsets)
    return frequent_itemsets
def fp_growth(transactions: List[List[str]], min_support: int) -> List[List[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions or min_support < 1:
        return []
    frequent_itemsets = find_frequent_itemsets(transactions, min_support)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
