def gini_impurity(y: np.ndarray) -> float:
    """Calculate the Gini impurity of a set of class labels."""
    if len(y) == 0:
        return 0.0
    counts = np.bincount(y)
    probabilities = counts / len(y)
    return 1.0 - np.sum(probabilities ** 2)
def best_split(X: np.ndarray, y: np.ndarray) -> Tuple[Optional[int], Optional[float], float]:
    """Find the best split (feature, threshold) that minimizes Gini impurity."""
    n_features = X.shape[1]
    best_gini = float('inf')
    (best_idx, best_threshold) = (None, None)
    for idx in range(n_features):
        unique_values = np.unique(X[:, idx])
        thresholds = []
        for i in range(len(unique_values) - 1):
            if unique_values[i] < unique_values[i + 1]:
                thresholds.append((unique_values[i] + unique_values[i + 1]) / 2)
        for threshold in thresholds:
            left_mask = X[:, idx] <= threshold
            y_left = y[left_mask]
            y_right = y[~left_mask]
            if len(y_left) == 0 or len(y_right) == 0:
                continue
            gini = len(y_left) / len(y) * gini_impurity(y_left) + len(y_right) / len(y) * gini_impurity(y_right)
            if gini < best_gini:
                best_gini = gini
                best_idx = idx
                best_threshold = threshold
    return (best_idx, best_threshold, best_gini)
class DecisionTreeNode:

    def __init__(self, feature_idx: Optional[int]=None, threshold: Optional[float]=None, left: Optional['DecisionTreeNode']=None, right: Optional['DecisionTreeNode']=None, value: Optional[int]=None, depth: int=0):
        self.feature_idx = feature_idx
        self.threshold = threshold
        self.left = left
        self.right = right
        self.value = value
        self.depth = depth

    def is_leaf(self) -> bool:
        return self.value is not None
def build_tree(X: np.ndarray, y: np.ndarray, depth: int, max_depth: Optional[int]) -> DecisionTreeNode:
    """Recursively build the decision tree."""
    if len(np.unique(y)) == 1:
        return DecisionTreeNode(value=int(y[0]), depth=depth)
    if max_depth is not None and depth >= max_depth:
        return DecisionTreeNode(value=int(np.bincount(y).argmax()), depth=depth)
    (feature_idx, threshold, best_gini) = best_split(X, y)
    if feature_idx is None:
        return DecisionTreeNode(value=int(np.bincount(y).argmax()), depth=depth)
    left_mask = X[:, feature_idx] <= threshold
    (X_left, y_left) = (X[left_mask], y[left_mask])
    (X_right, y_right) = (X[~left_mask], y[~left_mask])
    if len(y_left) == 0 or len(y_right) == 0:
        return DecisionTreeNode(value=int(np.bincount(y).argmax()), depth=depth)
    left_child = build_tree(X_left, y_left, depth + 1, max_depth)
    right_child = build_tree(X_right, y_right, depth + 1, max_depth)
    return DecisionTreeNode(feature_idx=feature_idx, threshold=threshold, left=left_child, right=right_child, depth=depth)
def predict_tree(node: DecisionTreeNode, x: np.ndarray) -> int:
    """Predict the class label for a single sample using the decision tree."""
    if node.is_leaf():
        return node.value
    if x[node.feature_idx] <= node.threshold:
        return predict_tree(node.left, x)
    else:
        return predict_tree(node.right, x)
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: Optional[int]=None) -> List[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""
    tree = build_tree(X_train, y_train, depth=0, max_depth=max_depth)
    return [predict_tree(tree, x) for x in X_test]
