import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_sup}
    item_order = sorted(frequent_items, key=lambda x: item_counts[x], reverse=True)
    item_to_index = {item: idx for (idx, item) in enumerate(item_order)}

    class FPNode:

        def __init__(self, item, count, parent):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}
            self.link = None

        def increment(self, count):
            self.count += count

    def insert_tree(items, node, item_order, item_to_index):
        if not items:
            return
        first = items[0]
        if first in node.children:
            node.children[first].increment(1)
        else:
            node.children[first] = FPNode(first, 1, node)
        insert_tree(items[1:], node.children[first], item_order, item_to_index)

    def update_header(node, header):
        while node.link is not None:
            node = node.link
        node.link = header

    def build_tree(transactions, item_order, item_to_index, min_sup):
        root = FPNode(None, 0, None)
        header_table = defaultdict(lambda : None)
        for transaction in transactions:
            filtered = [item for item in transaction if item in frequent_items]
            filtered.sort(key=lambda x: item_to_index[x], reverse=True)
            if filtered:
                insert_tree(filtered, root, item_order, item_to_index)
        for item in item_order:
            if item_counts[item] >= min_sup:
                if header_table[item] is None:
                    header_table[item] = FPNode(item, 0, None)
                update_header(root.children[item], header_table[item])
        return (root, header_table)
    filtered_transactions = []
    for transaction in transactions:
        filtered = [item for item in transaction if item in frequent_items]
        if filtered:
            filtered.sort(key=lambda x: item_to_index[x], reverse=True)
            filtered_transactions.append(filtered)
    (root, header_table) = build_tree(filtered_transactions, item_order, item_to_index, min_sup)

    def mine_tree(header_table, min_sup, prefix, frequent_itemsets):
        if not header_table:
            return
        items = sorted(header_table.keys())
        for item in items:
            new_prefix = prefix + (item,)
            frequent_itemsets.append(tuple(sorted(new_prefix)))
            conditional_patterns = []
            node = header_table[item]
            while node is not None:
                path = []
                parent = node.parent
                count = node.count
                while parent.item is not None:
                    path.append((parent.item, count))
                    parent = parent.parent
                if path:
                    conditional_patterns.append(path)
                node = node.link
            conditional_counts = Counter()
            for pattern in conditional_patterns:
                for (item, count) in pattern:
                    conditional_counts[item] += count
            conditional_items = [item for (item, count) in conditional_counts.items() if count >= min_sup]
            conditional_items.sort(key=lambda x: item_to_index[x], reverse=True)
            conditional_transactions = []
            for pattern in conditional_patterns:
                filtered = [(item, count) for (item, count) in pattern if item in conditional_items]
                if filtered:
                    filtered.sort(key=lambda x: item_to_index[x], reverse=True)
                    expanded = []
                    for (item, count) in filtered:
                        expanded.extend([item] * count)
                    conditional_transactions.append(expanded)
            if conditional_transactions:
                (_, new_header_table) = build_tree(conditional_transactions, conditional_items, item_to_index, min_sup)
                mine_tree(new_header_table, min_sup, new_prefix, frequent_itemsets)
    frequent_itemsets = []
    mine_tree(header_table, min_sup, (), frequent_itemsets)
    for item in frequent_items:
        frequent_itemsets.append((item,))
    frequent_itemsets = list(set(frequent_itemsets))
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets

