            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """Train a MaxEnt classifier using GIS and predict labels for test data."""
    feature_index = {}
    feature_counter = 0
    for i in range(X_train.shape[0]):
        x = X_train[i]
        y = y_train[i]
        for j in range(x.shape[0]):
            feature_value = x[j]
            if feature_value is not None:
                key = (j, feature_value, y)
                if key not in feature_index:
                    feature_index[key] = feature_counter
                    feature_counter += 1
    n_features = len(feature_index)
    n_classes = len(np.unique(y_train))
    empirical_counts = defaultdict(float)
    for i in range(X_train.shape[0]):
        x = X_train[i]
        y = y_train[i]
        for j in range(x.shape[0]):
            feature_value = x[j]
            if feature_value is not None:
                key = (j, feature_value, y)
                empirical_counts[key] += 1
    total_samples = X_train.shape[0]
    for key in empirical_counts:
        empirical_counts[key] /= total_samples
    weights = np.zeros(n_features)
    for iteration in range(n_iter):
        expected_counts = defaultdict(float)
        for i in range(X_train.shape[0]):
            x = X_train[i]
            log_Z = -np.inf
            best_log_prob = -np.inf
            best_y = None
            for y in range(n_classes):
                log_prob = 0.0
                for j in range(x.shape[0]):
                    feature_value = x[j]
                    if feature_value is not None:
                        key = (j, feature_value, y)
                        if key in feature_index:
                            log_prob += weights[feature_index[key]]
                log_prob -= 1
                expected_counts[y] += 1
                if log_prob > best_log_prob:
                    best_log_prob = log_prob
                    best_y = y
                if log_prob > log_Z:
                    log_Z = log_prob + math.log(1 + math.exp(log_Z - log_prob))
                else:
                    log_Z = log_prob + math.log(1 + math.exp(log_Z - log_prob))
                for j in range(x.shape[0]):
                    feature_value = x[j]
                    if feature_value is not None:
                        key = (j, feature_value, y)
                        if key in feature_index:
                            expected_counts[key] += math.exp(log_prob - log_Z)
        max_update = 0.0
        for key in empirical_counts:
            if key in expected_counts:
                delta = empirical_counts[key] - expected_counts[key]
                if delta != 0:
                    update = math.log(delta / (1 - delta)) if delta > 0 else math.log(delta / (1 - delta))
                    weights[feature_index[key]] += update
                    max_update = max(max_update, abs(update))
            elif empirical_counts[key] > 0:
                update = math.log(empirical_counts[key] / (1 - empirical_counts[key]))
                weights[feature_index[key]] += update
                max_update = max(max_update, abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for x in X_test:
        best_log_prob = -np.inf
        best_y = None
        for y in range(n_classes):
            log_prob = 0.0
            for j in range(x.shape[0]):
                feature_value = x[j]
                if feature_value is not None:
                    key = (j, feature_value, y)
                    if key in feature_index:
                        log_prob += weights[feature_index[key]]
            if log_prob > best_log_prob:
                best_log_prob = log_prob
                best_y = y
        predictions.append(best_y)
    return predictions
