from collections import Counter, defaultdict
from typing import List, Tuple, Dict, Set, Any
class FPTreeNode:

    def __init__(self, name: str, count: int, parent: 'FPTreeNode'):
        self.name = name
        self.count = count
        self.parent = parent
        self.children: Dict[str, 'FPTreeNode'] = {}
        self.link: 'FPTreeNode' = None

    def increment(self, count: int):
        self.count += count
def fp_growth(transactions: List[List[str]], min_support: int) -> List[List[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions:
        return []
    header_table = Counter()
    for transaction in transactions:
        header_table.update(transaction)
    header_table = {item: count for (item, count) in header_table.items() if count >= min_support}
    if not header_table:
        return []
    sorted_items = sorted(header_table.keys(), key=lambda x: (-header_table[x], x))
    item_to_index = {item: idx for (idx, item) in enumerate(sorted_items)}
    frequent_transactions = []
    for transaction in transactions:
        filtered = [item for item in transaction if item in header_table]
        if filtered:
            filtered.sort(key=lambda x: (-header_table[x], x))
            frequent_transactions.append(filtered)
    root = FPTreeNode('Null', 1, None)
    header_table = defaultdict(list)
    for transaction in frequent_transactions:
        add_transaction_to_tree(transaction, root, header_table)
    if len(header_table) == 0:
        return []
    frequent_itemsets: List[List[str]] = []
    sorted_header = sorted(header_table.items(), key=lambda x: item_to_index[x[0]])
    for (item, nodes) in sorted_header:
        conditional_pattern_base = get_conditional_pattern_base(nodes, root)
        conditional_frequencies = Counter()
        for (pattern, count) in conditional_pattern_base:
            conditional_frequencies.update({frozenset(pattern): count})
        if conditional_frequencies:
            for (pattern, count) in conditional_frequencies.items():
                if count >= min_support:
                    frequent_itemsets.append(sorted(list(pattern) + [item]))
            (conditional_tree, conditional_header) = build_conditional_tree(conditional_pattern_base, min_support)
            if conditional_header:
                conditional_frequent_itemsets = mine_tree(conditional_tree, conditional_header, [item], min_support)
                frequent_itemsets.extend(conditional_frequent_itemsets)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
def get_conditional_pattern_base(nodes: List[FPTreeNode], root: FPTreeNode) -> List[Tuple[Set[str], int]]:
    """Get the conditional pattern base for a given item."""
    conditional_pattern_base = []
    for node in reversed(nodes):
        pattern = []
        count = node.count
        parent = node.parent
        while parent and parent.name != 'Null':
            pattern.append(parent.name)
            parent = parent.parent
        if pattern:
            conditional_pattern_base.append((set(pattern), count))
    return conditional_pattern_base
def build_conditional_tree(pattern_base: List[Tuple[Set[str], int]], min_support: int) -> Tuple[FPTreeNode, Dict[str, List[FPTreeNode]]]:
    """Build a conditional FP-tree from a conditional pattern base."""
    frequencies = Counter()
    for (pattern, count) in pattern_base:
        for item in pattern:
            frequencies[item] += count
    frequencies = {item: count for (item, count) in frequencies.items() if count >= min_support}
    if not frequencies:
        return (None, None)
    sorted_items = sorted(frequencies.keys(), key=lambda x: (-frequencies[x], x))
    conditional_transactions = []
    for (pattern, count) in pattern_base:
        filtered = [item for item in pattern if item in frequencies]
        if filtered:
            filtered.sort(key=lambda x: (-frequencies[x], x))
            for _ in range(count):
                conditional_transactions.append(filtered)
    conditional_root = FPTreeNode('Null', 1, None)
    conditional_header = defaultdict(list)
    for transaction in conditional_transactions:
        add_transaction_to_tree(transaction, conditional_root, conditional_header)
    return (conditional_root, conditional_header)
def mine_tree(tree: FPTreeNode, header_table: Dict[str, List[FPTreeNode]], prefix: List[str], min_support: int) -> List[List[str]]:
    """Recursively mine the FP-tree to find all frequent itemsets."""
    frequent_itemsets: List[List[str]] = []
    sorted_items = sorted(header_table.items(), key=lambda x: x[0])
    for (item, nodes) in sorted_items:
        new_itemset = sorted(prefix + [item])
        frequent_itemsets.append(new_itemset)
        conditional_pattern_base = get_conditional_pattern_base(nodes, tree)
        conditional_frequencies = Counter()
        for (pattern, count) in conditional_pattern_base:
            conditional_frequencies.update({frozenset(pattern): count})
        if conditional_frequencies:
            (conditional_tree, conditional_header) = build_conditional_tree(conditional_pattern_base, min_support)
            if conditional_header:
                conditional_frequent_itemsets = mine_tree(conditional_tree, conditional_header, new_itemset, min_support)
                frequent_itemsets.extend(conditional_frequent_itemsets)
    return frequent_itemsets
