import numpy as np
import numpy as np
from typing import Optional, List, Tuple
def gini_impurity(y: np.ndarray) -> float:
    """Calculate Gini impurity for a set of class labels."""
    if len(y) == 0:
        return 0.0
    counts = np.bincount(y)
    probabilities = counts / len(y)
    return 1.0 - np.sum(probabilities ** 2)
def majority_vote(y: np.ndarray) -> int:
    """Return the majority class label."""
    return np.argmax(np.bincount(y))
def find_best_split(X: np.ndarray, y: np.ndarray) -> Tuple[Optional[int], Optional[float], float]:
    """Find the best feature and threshold to split on, maximizing Gini gain."""
    best_gini = float('inf')
    best_feature = None
    best_threshold = None
    for feature_idx in range(X.shape[1]):
        unique_vals = np.unique(X[:, feature_idx])
        thresholds = []
        for i in range(len(unique_vals) - 1):
            if unique_vals[i] != unique_vals[i + 1]:
                thresholds.append((unique_vals[i] + unique_vals[i + 1]) / 2)
        for threshold in thresholds:
            left_mask = X[:, feature_idx] <= threshold
            right_mask = X[:, feature_idx] > threshold
            y_left = y[left_mask]
            y_right = y[right_mask]
            if len(y_left) == 0 or len(y_right) == 0:
                continue
            gini_left = gini_impurity(y_left)
            gini_right = gini_impurity(y_right)
            weighted_gini = len(y_left) / len(y) * gini_left + len(y_right) / len(y) * gini_right
            if weighted_gini < best_gini:
                best_gini = weighted_gini
                best_feature = feature_idx
                best_threshold = threshold
    if best_gini == float('inf'):
        return (None, None, 0.0)
    return (best_feature, best_threshold, best_gini)
class DecisionTreeNode:

    def __init__(self):
        self.feature = None
        self.threshold = None
        self.left = None
        self.right = None
        self.prediction = None
        self.depth = 0

    def is_leaf(self):
        return self.feature is None
def build_tree(X: np.ndarray, y: np.ndarray, depth: int, max_depth: Optional[int]):
    """Recursively build the decision tree."""
    node = DecisionTreeNode()
    node.depth = depth
    if np.all(y == y[0]):
        node.prediction = y[0]
        return node
    if max_depth is not None and depth >= max_depth:
        node.prediction = majority_vote(y)
        return node
    (feature, threshold, _) = find_best_split(X, y)
    if feature is None:
        node.prediction = majority_vote(y)
        return node
    node.feature = feature
    node.threshold = threshold
    left_mask = X[:, feature] <= threshold
    right_mask = X[:, feature] > threshold
    if np.any(left_mask):
        node.left = build_tree(X[left_mask], y[left_mask], depth + 1, max_depth)
    else:
        node.left = DecisionTreeNode()
        node.left.prediction = majority_vote(y)
    if np.any(right_mask):
        node.right = build_tree(X[right_mask], y[right_mask], depth + 1, max_depth)
    else:
        node.right = DecisionTreeNode()
        node.right.prediction = majority_vote(y)
    return node
def predict_tree(node: DecisionTreeNode, x: np.ndarray) -> int:
    """Predict label for a single sample using the tree."""
    if node.is_leaf():
        return node.prediction
    if x[node.feature] <= node.threshold:
        return predict_tree(node.left, x)
    else:
        return predict_tree(node.right, x)
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: Optional[int]=None) -> List[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""
    tree = build_tree(X_train, y_train, depth=0, max_depth=max_depth)
    predictions = [predict_tree(tree, x) for x in X_test]
    return predictions
