def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """

    class FPNode:

        def __init__(self, item, count, parent):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}
            self.link = None
    item_counts = Counter()
    for transaction in transactions:
        for item in transaction:
            item_counts[item] += 1
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_sup}
    if not frequent_items:
        return []
    sorted_items = sorted(frequent_items, key=lambda x: item_counts[x], reverse=True)
    item_order = {item: idx for (idx, item) in enumerate(sorted_items)}

    def sort_transaction(transaction):
        return sorted((item for item in transaction if item in frequent_items), key=lambda x: item_order[x], reverse=True)
    sorted_transactions = [sort_transaction(transaction) for transaction in transactions]
    sorted_transactions = [t for t in sorted_transactions if len(t) > 0]

    def build_tree(transactions):
        root = FPNode(None, 1, None)
        header_table = defaultdict(list)
        for transaction in transactions:
            if not transaction:
                continue
            update_tree(transaction, root, header_table)
        for item in list(header_table.keys()):
            if sum((node.count for node in header_table[item])) < min_sup:
                del header_table[item]
        return (root, header_table)

    def update_tree(items, node, header_table):
        if not items:
            return
        item = items[0]
        rest = items[1:]
        if item in node.children:
            node.children[item].count += 1
        else:
            node.children[item] = FPNode(item, 1, node)
            if item not in header_table:
                header_table[item] = []
            header_table[item].append(node.children[item])
        update_tree(rest, node.children[item], header_table)
    (root, header_table) = build_tree(sorted_transactions)
    frequent_itemsets = [tuple([item]) for item in sorted_items]

    def mine_tree(header_table, prefix, frequent_itemsets):
        items = sorted(header_table.keys(), key=lambda x: item_order[x])
        for item in items:
            new_prefix = tuple(sorted(list(prefix) + [item]))
            frequent_itemsets.append(new_prefix)
            conditional_patterns = []
            for node in header_table[item]:
                path = []
                current = node.parent
                while current.item is not None:
                    path.append((current.item, current.count))
                    current = current.parent
                if path:
                    path.reverse()
                    pruned_path = [(i, c) for (i, c) in path if i not in prefix]
                    if pruned_path:
                        conditional_patterns.append((pruned_path, node.count))

            def flatten_conditional_patterns(patterns):
                counter = Counter()
                for (path, count) in patterns:
                    for (item, _) in path:
                        counter[item] += count
                return counter
            item_counts = flatten_conditional_patterns(conditional_patterns)
            frequent_items = {item for (item, count) in item_counts.items() if count >= min_sup}
            if not frequent_items:
                continue
            sorted_items = sorted(frequent_items, key=lambda x: item_counts[x], reverse=True)
            item_order_cond = {item: idx for (idx, item) in enumerate(sorted_items)}

            def sort_path(path, count):
                items = [(item, count) for (item, _) in path if item in frequent_items]
                return sorted(items, key=lambda x: item_order_cond[x[0]], reverse=True)
            conditional_transactions = []
            for (path, count) in conditional_patterns:
                sorted_path = sort_path(path, count)
                if sorted_path:
                    conditional_transactions.append(sorted_path)
            if not conditional_transactions:
                continue
            (cond_root, cond_header_table) = build_tree(conditional_transactions)
            if cond_header_table:
                mine_tree(cond_header_table, new_prefix, frequent_itemsets)
    mine_tree(header_table, (), frequent_itemsets)
    frequent_itemsets = [tuple(sorted(itemset)) for itemset in frequent_itemsets]
    frequent_itemsets = sorted(frequent_itemsets, key=lambda x: (len(x), x))
    return frequent_itemsets
