import numpy as np
def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation function or its derivative (sigmoid).

    Args:
        x: A scalar, list, or NumPy array of real numbers.
        deriv: Boolean flag. If True, returns the derivative (sigmoid) instead of SoftPlus.

    Returns:
        A Python list of rounded values (or a single float if input is scalar).
    """
    x = np.array(x, dtype=np.float64)
    if deriv:
        return np.round(1 / (1 + np.exp(-x)), 4).tolist()
    else:
        with np.errstate(over='ignore'):
            return np.round(np.log1p(np.exp(-np.abs(x))) + np.maximum(x, 0), 4).tolist()

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    n = len(S)
    if n == 0:
        return -1
    if len(A) != n or any((len(row) != n for row in A)):
        return -1
    if len(B) != n or any((len(row) == 0 for row in B)) or any((len(row) != len(B[0]) for row in B)):
        return -1
    m = len(B[0])
    if m == 0:
        return -1
    if not observations:
        return -1
    for obs in observations:
        if not 0 <= obs < m:
            return -1
    T = len(observations)
    alpha = np.zeros((T, n))
    for i in range(n):
        alpha[0][i] = S[i] * B[i][observations[0]]
    for t in range(1, T):
        for i in range(n):
            alpha[t][i] = sum((alpha[t - 1][j] * A[j][i] for j in range(n))) * B[i][observations[t]]
    likelihood = sum((alpha[T - 1][i] for i in range(n)))
    return round(likelihood, 4)

