        if mu.shape[0] != d:
            raise ValueError(f'mu must be a scalar or a vector of length {d}')
    if V is None:
        V = np.eye(d)
    elif isinstance(V, (int, float)):
        V = V * np.eye(d)
    elif isinstance(V, np.ndarray):
        if V.ndim == 1:
            V = np.diag(V)
        elif V.ndim == 2 and V.shape != (d, d):
            raise ValueError(f'V must be a scalar, 1-D array of length {d}, or a {d}x{d} matrix')
    else:
        raise TypeError('V must be a scalar, 1-D array, or 2-D array')
    V = (V + V.T) / 2
    try:
        np.linalg.cholesky(V)
    except np.linalg.LinAlgError:
        raise ValueError('V must be symmetric and positive definite')
    V_inv = np.linalg.inv(V)
    XtX = X.T @ X
    Sigma_p_inv = V_inv + XtX
    Sigma_p = np.linalg.inv(Sigma_p_inv)
    mu_p = Sigma_p @ (V_inv @ mu + X.T @ y)
    y_pred = X_new @ mu_p
    return [round(val, 4) for val in y_pred.tolist()]

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Calculate the Root Mean Squared Logarithmic Error (RMSLE)."""
    if len(actual) != len(predicted):
        return -1
    if any((x < 0 for x in actual)) or any((x < 0 for x in predicted)):
        return -1
    n = len(actual)
    log_actual = np.log1p(actual)
    log_predicted = np.log1p(predicted)
    squared_errors = (log_actual - log_predicted) ** 2
    rmsle = np.sqrt(np.mean(squared_errors))
    return round(rmsle, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    x = np.array(x, dtype=float)
    if order not in {0, 1, 2}:
        return -1
    s = 1 / (1 + np.exp(-x))
    if order == 0:
        result = s
    elif order == 1:
        result = s * (1 - s)
    elif order == 2:
        result = s * (1 - s) * (1 - 2 * s)
    result = np.round(result, 4)
    if x.ndim == 0:
        return float(result)
    else:
        return result.tolist()

import itertools
from collections import defaultdict
from typing import List, Tuple, Hashable
def apriori_frequent_itemsets(transactions: List[List[Hashable]], min_sup: float) -> List[Tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    if not 0 < min_sup <= 1:
        raise ValueError('min_sup must be in (0, 1]')
    num_transactions = len(transactions)
    min_support_count = int(min_sup * num_transactions)
    item_counts = defaultdict(int)
    for transaction in transactions:
        for item in set(transaction):
            item_counts[frozenset([item])] += 1
    frequent_itemsets = {1: {frozenset([item]) for (item, count) in item_counts.items() if count >= min_support_count}}
    result = []
    k = 2
    while True:
        candidates = set()
        prev_frequent = frequent_itemsets[k - 1]
        for itemset1 in prev_frequent:
            for itemset2 in prev_frequent:
                if len(itemset1.symmetric_difference(itemset2)) == 2:
                    candidate = itemset1.union(itemset2)
                    if all((frozenset(subset) in prev_frequent for subset in itertools.combinations(candidate, k - 1))):
                        candidates.add(candidate)
        if not candidates:
            break
        candidate_counts = defaultdict(int)
        for transaction in transactions:
            transaction_set = set(transaction)
            for candidate in candidates:
                if candidate.issubset(transaction_set):
                    candidate_counts[candidate] += 1
        frequent_k = {candidate for (candidate, count) in candidate_counts.items() if count >= min_support_count}
        if not frequent_k:
            break
        frequent_itemsets[k] = frequent_k
        for itemset in frequent_k:
            result.append(tuple(sorted(itemset)))
        k += 1
    result.sort(key=lambda x: (len(x), x))
    return result
