import numpy as np
from typing import List, Set, Tuple
def dbscan(data: np.ndarray, eps: float, min_samples: int) -> List[int]:
    """Cluster *data* with the DBSCAN algorithm.

    Parameters
    ----------
    data : np.ndarray
        Two-dimensional array where each row is a sample and columns are
        features.
    eps : float
        Maximum radius of the neighborhood to be considered reachable.
    min_samples : int
        Minimum number of samples (including the point itself) required to form
        a dense region.

    Returns
    -------
    list[int]
        Cluster labels for each sample. Noise points are labeled ‑1 and cluster
        indices start at 0 and increase sequentially in discovery order.
    """
    if data.size == 0:
        return []
    n_samples = data.shape[0]
    labels = [-1] * n_samples
    cluster_id = 0

    def euclidean_distance(p1: np.ndarray, p2: np.ndarray) -> float:
        return np.sqrt(np.sum((p1 - p2) ** 2))

    def get_neighbors(point_idx: int) -> List[int]:
        """Return indices of points within distance eps of the given point."""
        neighbors = []
        for j in range(n_samples):
            if euclidean_distance(data[point_idx], data[j]) < eps:
                neighbors.append(j)
        return neighbors
    for i in range(n_samples):
        if labels[i] != -1:
            continue
        neighbors = get_neighbors(i)
        if len(neighbors) < min_samples:
            labels[i] = -1
        else:
            cluster_id += 1
            labels[i] = cluster_id
            seed_set = [i]
            for j in range(len(seed_set)):
                current_point = seed_set[j]
                current_neighbors = get_neighbors(current_point)
                if len(current_neighbors) >= min_samples:
                    for neighbor in current_neighbors:
                        if labels[neighbor] == -1:
                            labels[neighbor] = cluster_id
                            seed_set.append(neighbor)
                        elif labels[neighbor] == 0:
                            labels[neighbor] = cluster_id
                            seed_set.append(neighbor)
            for point in seed_set:
                if labels[point] == 0:
                    labels[point] = cluster_id
    for i in range(n_samples):
        if labels[i] == 0:
            labels[i] = -1
    return labels

from collections import Counter, defaultdict
import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """

    class FPNode:

        def __init__(self, item, count, parent):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}
            self.link = None
    item_counts = Counter()
    for transaction in transactions:
        for item in transaction:
            item_counts[item] += 1
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_sup}
    if not frequent_items:
        return []
    sorted_items = sorted(frequent_items, key=lambda x: item_counts[x], reverse=True)
    item_order = {item: idx for (idx, item) in enumerate(sorted_items)}

    def sort_transaction(transaction):
        return sorted((item for item in transaction if item in frequent_items), key=lambda x: item_order[x], reverse=True)
    sorted_transactions = [sort_transaction(transaction) for transaction in transactions]
    sorted_transactions = [t for t in sorted_transactions if len(t) > 0]

    def build_tree(transactions):
        root = FPNode(None, 1, None)
        header_table = defaultdict(list)
        for transaction in transactions:
            if not transaction:
                continue
            update_tree(transaction, root, header_table)
        for item in list(header_table.keys()):
            if sum((node.count for node in header_table[item])) < min_sup:
                del header_table[item]
        return (root, header_table)

    def update_tree(items, node, header_table):
        if not items:
            return
        item = items[0]
        rest = items[1:]
        if item in node.children:
            node.children[item].count += 1
        else:
            node.children[item] = FPNode(item, 1, node)
            if item not in header_table:
                header_table[item] = []
            header_table[item].append(node.children[item])
        update_tree(rest, node.children[item], header_table)
    (root, header_table) = build_tree(sorted_transactions)
    frequent_itemsets = [tuple([item]) for item in sorted_items]

    def mine_tree(header_table, prefix, frequent_itemsets):
        items = sorted(header_table.keys(), key=lambda x: item_order[x])
        for item in items:
            new_prefix = tuple(sorted(list(prefix) + [item]))
            frequent_itemsets.append(new_prefix)
            conditional_patterns = []
            for node in header_table[item]:
                path = []
                current = node.parent
                while current.item is not None:
                    path.append((current.item, current.count))
                    current = current.parent
                if path:
                    path.reverse()
                    pruned_path = [(i, c) for (i, c) in path if i not in prefix]
                    if pruned_path:
                        conditional_patterns.append((pruned_path, node.count))

            def flatten_conditional_patterns(patterns):
                counter = Counter()
                for (path, count) in patterns:
                    for (item, _) in path:
                        counter[item] += count
                return counter
            item_counts = flatten_conditional_patterns(conditional_patterns)
            frequent_items = {item for (item, count) in item_counts.items() if count >= min_sup}
            if not frequent_items:
                continue
            sorted_items = sorted(frequent_items, key=lambda x: item_counts[x], reverse=True)
            item_order_cond = {item: idx for (idx, item) in enumerate(sorted_items)}

            def sort_path(path, count):
                items = [(item, count) for (item, _) in path if item in frequent_items]
                return sorted(items, key=lambda x: item_order_cond[x[0]], reverse=True)
            conditional_transactions = []
            for (path, count) in conditional_patterns:
                sorted_path = sort_path(path, count)
                if sorted_path:
                    conditional_transactions.append(sorted_path)
            if not conditional_transactions:
                continue
            (cond_root, cond_header_table) = build_tree(conditional_transactions)
            if cond_header_table:
                mine_tree(cond_header_table, new_prefix, frequent_itemsets)
    mine_tree(header_table, (), frequent_itemsets)
    frequent_itemsets = [tuple(sorted(itemset)) for itemset in frequent_itemsets]
    frequent_itemsets = sorted(frequent_itemsets, key=lambda x: (len(x), x))
    return frequent_itemsets

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def process(x):
        if isinstance(x, (float, int)):
            return round(x * (1 - x), 4)
        elif isinstance(x, list):
            return [process(item) for item in x]
        elif isinstance(x, np.ndarray):
            return np.round(x * (1 - x), 4).tolist()
        else:
            raise TypeError('Unsupported input type')
    if isinstance(z, (float, int)):
        return round(z * (1 - z), 4)
    elif z == []:
        return []
    else:
        return process(z)

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for (i, child) in enumerate(root.child):
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """Implements a MaxEnt classifier using Generalized Iterative Scaling (GIS)."""
    (num_samples, num_features) = X_train.shape
    num_labels = len(np.unique(y_train))
    label_set = np.unique(y_train)
    feature_index = {}
    index = 0
    for i in range(num_features):
        for val in np.unique(X_train[:, i]):
            for label in label_set:
                key = (i, val, label)
                if key not in feature_index:
                    feature_index[key] = index
                    index += 1
    num_features_total = index
    weights = np.zeros(num_features_total)
    empirical_counts = defaultdict(int)
    for i in range(num_samples):
        x = X_train[i]
        y = y_train[i]
        for j in range(num_features):
            val = x[j]
            feature_key = (j, val, y)
            empirical_counts[feature_key] += 1
    empirical_counts_array = np.zeros(num_features_total)
    for (key, count) in empirical_counts.items():
        empirical_counts_array[feature_index[key]] = count
    for _ in range(n_iter):
        expected_counts = np.zeros(num_features_total)
        for i in range(num_samples):
            x = X_train[i]
            y = y_train[i]
            log_probs = np.zeros(num_labels)
            for (label_idx, label) in enumerate(label_set):
                score = 0.0
                for j in range(num_features):
                    val = x[j]
                    key = (j, val, label)
                    if key in feature_index:
                        score += weights[feature_index[key]]
                log_probs[label_idx] = score
            max_score = log_probs.max()
            log_Z = math.log(np.sum(np.exp(log_probs - max_score))) + max_score
            probs = np.exp(log_probs - log_Z)
            for (label_idx, label) in enumerate(label_set):
                for j in range(num_features):
                    val = x[j]
                    key = (j, val, label)
                    if key in feature_index:
                        expected_counts[feature_index[key]] += probs[label_idx]
        delta = empirical_counts_array - expected_counts
        max_update = np.max(np.abs(delta))
        if max_update < epsilon:
            break
        weights += delta
    predictions = []
    for x in X_test:
        scores = {}
        for label in label_set:
            score = 0.0
            for j in range(num_features):
                val = x[j]
                key = (j, val, label)
                if key in feature_index:
                    score += weights[feature_index[key]]
            scores[label] = score
        predicted_label = max(scores, key=scores.get)
        predictions.append(predicted_label)
    return predictions
