def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemset (stored as a tuple of
        sorted items) to its absolute support count.  If no itemset reaches
        the threshold the function must return an empty dictionary.
    """
    N = len(transactions)
    if N == 0:
        return {}
    trans_sets = [set(t) for t in transactions]
    item_counts = Counter()
    for t in trans_sets:
        for item in t:
            item_counts[item] += 1
    threshold = math.ceil(minsup * N)
    freq_1 = {tuple([item]): count for (item, count) in item_counts.items() if count >= threshold}
    if not freq_1:
        return {}
    result = freq_1.copy()
    k = 2
    current_freq = freq_1
    while current_freq:
        candidates = set()
        items = list(current_freq.keys())
        for i in range(len(items)):
            for j in range(i + 1, len(items)):
                itemset1 = items[i]
                itemset2 = items[j]
                if all((itemset1[i] == itemset2[i] for i in range(k - 2))):
                    merged = tuple(sorted(set(itemset1) | set(itemset2)))
                    if len(merged) == k:
                        candidates.add(merged)
        candidates = [c for c in candidates if all((tuple(sorted(c[:i] + c[i + 1:])) in current_freq for i in range(k)))]
        freq_k = {}
        for candidate in candidates:
            count = 0
            for t in trans_sets:
                if set(candidate).issubset(t):
                    count += 1
            if count >= threshold:
                freq_k[candidate] = count
        if not freq_k:
            break
        result.update(freq_k)
        current_freq = freq_k
        k += 1
    return result
