def adaboost_1d_predict(x_train: list[float], y_train: list[int], x_test: list[float], epsilon: float=0.0) -> list[int]:
    """Trains a 1-D AdaBoost ensemble and returns predictions.

    Parameters
    ----------
    x_train : list[float]
        Training sample values (one-dimensional).
    y_train : list[int]
        Labels corresponding to *x_train* (each value must be 1 or –1).
    x_test : list[float]
        Sample values to classify after training.
    epsilon : float, default 0.0
        Upper bound on the allowed training error.  Training stops once
        the ensemble’s training error ≤ *epsilon*.

    Returns
    -------
    list[int]
        Predicted labels (1 or –1) for every value in *x_test*.
    """
    n = len(x_train)
    D = np.ones(n) / n
    ensemble = []
    while True:
        best_error = float('inf')
        best_stump = None
        sorted_indices = np.argsort(x_train)
        sorted_x = [x_train[i] for i in sorted_indices]
        sorted_y = [y_train[i] for i in sorted_indices]
        thresholds = []
        for i in range(n - 1):
            thresholds.append((sorted_x[i] + sorted_x[i + 1]) / 2)
        for theta in thresholds:
            for d in [0, 1]:
                error = 0
                predictions = []
                for i in range(n):
                    if d == 0 and x_train[i] <= theta:
                        pred = 1
                    elif d == 1 and x_train[i] > theta:
                        pred = 1
                    else:
                        pred = -1
                    predictions.append(pred)
                    if pred != y_train[i]:
                        error += D[i]
                if error < best_error:
                    best_error = error
                    best_stump = (d, theta)
                    best_predictions = predictions
        if best_error == 0:
            alpha = float('inf')
        else:
            alpha = 0.5 * math.log((1 - best_error) / best_error)
        ensemble.append((best_stump[0], best_stump[1], alpha))
        for i in range(n):
            if best_predictions[i] == y_train[i]:
                D[i] *= math.exp(-alpha)
            else:
                D[i] *= math.exp(alpha)
        D = D / np.sum(D)
        ensemble_prediction = np.zeros(n)
        for (d, theta, alpha) in ensemble:
            for i in range(n):
                if d == 0 and x_train[i] <= theta:
                    pred = 1
                elif d == 1 and x_train[i] > theta:
                    pred = 1
                else:
                    pred = -1
                ensemble_prediction[i] += alpha * pred
        final_train_pred = np.sign(ensemble_prediction)
        train_error = np.mean(final_train_pred != np.array(y_train))
        if train_error <= epsilon:
            break
    test_predictions = []
    for x in x_test:
        score = 0
        for (d, theta, alpha) in ensemble:
            if d == 0 and x <= theta:
                pred = 1
            elif d == 1 and x > theta:
                pred = 1
            else:
                pred = -1
            score += alpha * pred
        test_predictions.append(1 if score >= 0 else -1)
    return test_predictions

